INSERT INTO `sy_article` (`id`, `subject`, `lastmodifytime`, `author`, `message`, `createtime`) VALUES
(4, '关于我院综合测评动员大会的通知', 1464853853, '思源宣传部', '<p><span style="font-size:24px;">&nbsp; 新学期即将开始，综合测评、家庭经济困难学生认定等工作将陆续开展，</span><span style="font-size:24px;">现将新学期开学初会议的安排预备通知如下：</span></p><p><span style="font-size:24px;">一、参加人员：</span></p><p><span style="font-size:24px;">&nbsp;&nbsp;&nbsp; 各年级辅导员、各年级级长、各班级负责人（班长或者团支书）、学院各学生组织负责人1名，新生助班1名，思源工作室主要学生干部。</span></p><p><span style="font-size:24px;">二、会议时间和地点</span></p><p><span style="font-size:24px;">&nbsp;&nbsp;&nbsp; 9月13日晚上19点30分 院楼500</span></p><p><span style="font-size:24px;">三、会议内容</span></p><p><span style="font-size:24px;">&nbsp;&nbsp;&nbsp; 综合测评动员会及家庭经济困难学生认定工作。</span></p><p>&nbsp;</p><p><span style="font-size:24px;">请上述与会人员提前5分钟入场，各年级级委做好本年级班级负责人的签到工作。</span></p><p><br/></p>', 16),
(5, '关于做好我院2014-2015学年度学生综合测评及评优工作的通知', 1464853926, '思源宣传部', '<p style="margin-bottom:0;margin-top:0"><span style="font-size:16px;font-family:SimHei">201</span><span style="font-size:16px;font-family:SimHei">2</span><span style="font-size:16px;font-family:SimHei">、</span><span style="font-size:16px;font-family:SimHei">201</span><span style="font-size:16px;font-family:SimHei">3</span><span style="font-size:16px;font-family:SimHei">和</span><span style="font-size:16px;font-family:SimHei">201</span><span style="font-size:16px;font-family:SimHei">4</span><span style="font-size:16px;font-family:SimHei">级及各学生班：</span></p><p style="margin-bottom:13px;text-align:left;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;根据校学生工作处的要求，现开展上一年度综合测评工作。为保证我院</span><span style="font-size:16px;font-family:&#39;宋体&#39;">201</span><span style="font-size:16px;font-family:&#39;宋体&#39;">4</span><span style="font-size:16px;font-family:&#39;宋体&#39;">—</span><span style="font-size:16px;font-family:&#39;宋体&#39;">201</span><span style="font-size:16px;font-family:&#39;宋体&#39;">5</span><span style="font-size:16px;font-family:&#39;宋体&#39;">学年度学生德智体综合测评与评奖评优工作顺利进行，根据《华南农业大学本科生综合测评及评优实施办法》（华农党发</span><span style="font-size:16px;font-family:&#39;宋体&#39;">[2011]32</span><span style="font-size:16px;font-family:&#39;宋体&#39;">号）（以下简称《办法》）和《数学与信息学院、软件学院关于</span><span style="font-size:16px;font-family:&#39;宋体&#39;">&lt;</span><span style="font-size:16px;font-family:&#39;宋体&#39;">华南农业大学本科生综合测评及评优实施办法</span><span style="font-size:16px;font-family:&#39;宋体&#39;">&gt;</span><span style="font-size:16px;font-family:&#39;宋体&#39;">的附加实施细则》（</span><span style="font-size:16px;font-family:&#39;宋体&#39;">http://info.scau.edu.cn/news-3762.asp</span><span style="font-size:16px;font-family:&#39;宋体&#39;">）的文件精神，现将有关事项通知如下。请各年级、班级遵照执行。</span></p><p style="margin-bottom:13px;text-align:left;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">&nbsp;</span></p><p style="margin-bottom:13px;text-align:left;margin-left:14px;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">一、学院综合测评工作组</span></p><p style="margin-bottom:13px;text-align:left;margin-left:14px;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">1</span><span style="font-size:16px;font-family:&#39;宋体&#39;">、数学与信息学院、软件学院成立本科生综合测评领导小组</span></p><p style="margin-bottom:13px;text-align:left;margin-left:14px;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">组长：郭灼</span><span style="font-size:16px;font-family:&#39;宋体&#39;">(</span><span style="font-size:16px;font-family:&#39;宋体&#39;">党委副书记</span><span style="font-size:16px;font-family:&#39;宋体&#39;">)</span></p><p style="margin-bottom:13px;text-align:left;margin-left:14px;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">组员：詹瑾妮、梁辉、毛丹娟、戴文浪、</span><span style="font-size:16px;font-family:&#39;宋体&#39;">陈思</span><span style="font-size:16px;font-family:&#39;宋体&#39;">、</span><span style="font-size:16px;font-family:&#39;宋体&#39;">刘树鑫、余丹华、倪慧娜、</span><span style="font-size:16px;font-family:&#39;宋体&#39;">黄经纬、各系主任。</span></p><p style="margin-bottom:13px;text-align:left;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">2</span><span style="font-size:16px;font-family:&#39;宋体&#39;">、年级成立综合测评小组</span></p><p style="margin-bottom:13px;text-align:left;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">年级综合测评小组组员：年级辅导员、班主任、学生党员代表、学生代表、级委委员、各班班长。</span></p><p style="margin-bottom:13px;text-align:left;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">3.</span><span style="font-size:16px;font-family:&#39;宋体&#39;">班级综合测评小组</span></p><p style="margin-bottom:13px;text-align:left;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">&nbsp;</span><span style="font-size:16px;font-family:&#39;宋体&#39;">班委会和</span><span style="font-size:16px;font-family:&#39;宋体&#39;">2</span><span style="font-size:16px;font-family:&#39;宋体&#39;">名非班级干部</span></p><p style="margin-bottom:13px;text-align:left;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">&nbsp;</span><span style="font-size:16px;font-family:&#39;宋体&#39;">二、工作要求及注意事项</span></p><p style="margin-bottom:13px;text-align:left;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">1</span><span style="font-size:16px;font-family:&#39;宋体&#39;">、班主任必须参加班级综合测评，并在学生综合评价表上签署意见。</span></p><p style="margin-bottom:13px;text-align:left;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">2</span><span style="font-size:16px;font-family:&#39;宋体&#39;">、</span><span style="font-size:16px;font-family:&#39;宋体&#39;">201</span><span style="font-size:16px;font-family:&#39;宋体&#39;">4</span><span style="font-size:16px;font-family:&#39;宋体&#39;">—</span><span style="font-size:16px;font-family:&#39;宋体&#39;">201</span><span style="font-size:16px;font-family:&#39;宋体&#39;">5</span><span style="font-size:16px;font-family:&#39;宋体&#39;">学年</span><span style="font-size:16px;font-family:&#39;宋体&#39;">度综合测评时间范围界定为2014年9月10日—2015年</span><span style="font-size:16px;font-family:&#39;宋体&#39;">8</span><span style="font-size:16px;font-family:&#39;宋体&#39;">月</span><span style="font-size:16px;font-family:&#39;宋体&#39;">31</span><span style="font-size:16px;font-family:&#39;宋体&#39;">日。</span></p><p style="margin-bottom:13px;text-align:left;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">3</span><span style="font-size:16px;font-family:&#39;宋体&#39;">、《办法》中学生的本学年平均绩点分值精确到小数点后两位数，学业成绩以教务处公布的该学年度学生的平均学分绩点为准。</span></p><p style="margin-bottom:13px;text-align:left;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">4</span><span style="font-size:16px;font-family:&#39;宋体&#39;">、开展测评的工作时间：</span><span style="font-size:16px;font-family:&#39;宋体&#39;">2014</span><span style="font-size:16px;font-family:&#39;宋体&#39;">年</span><span style="font-size:16px;font-family:&#39;宋体&#39;">9</span><span style="font-size:16px;font-family:&#39;宋体&#39;">月</span><span style="font-size:16px;font-family:&#39;宋体&#39;">1</span><span style="font-size:16px;font-family:&#39;宋体&#39;">2</span><span style="font-size:16px;font-family:&#39;宋体&#39;">日—</span><span style="font-size:16px;font-family:&#39;宋体&#39;">2014</span><span style="font-size:16px;font-family:&#39;宋体&#39;">年</span><span style="font-size:16px;font-family:&#39;宋体&#39;">9</span><span style="font-size:16px;font-family:&#39;宋体&#39;">月</span><span style="font-size:16px;font-family:&#39;宋体&#39;">30</span><span style="font-size:16px;font-family:&#39;宋体&#39;">日，各年级和班级学生填写《华南农业大学学生综合测评个人自评报告》（附件</span><span style="font-size:16px;font-family:&#39;宋体&#39;">1</span><span style="font-size:16px;font-family:&#39;宋体&#39;">）等表格材料，在规定时间内完成评审工作，经学院审核公示后，上报学校学生工作处。具体的时间安排见附件“数学与信息（软件）学院</span><span style="font-size:16px;font-family:&#39;宋体&#39;">201</span><span style="font-size:16px;font-family:&#39;宋体&#39;">4</span><span style="font-size:16px;font-family:&#39;宋体&#39;">—</span><span style="font-size:16px;font-family:&#39;宋体&#39;">201</span><span style="font-size:16px;font-family:&#39;宋体&#39;">5</span><span style="font-size:16px;font-family:&#39;宋体&#39;">学年度学生综合测评工作时间安排表”。</span></p><p style="margin-bottom:13px;text-align:left;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">5</span><span style="font-size:16px;font-family:&#39;宋体&#39;">、年级范围内各等级获奖人数计算四舍五入至整数。</span></p><p style="margin-bottom:13px;text-align:left;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">6</span><span style="font-size:16px;font-family:&#39;宋体&#39;">、“创优”呈批表必须由辅导员填写、签名，由学院整合按一等奖、二等奖、三等奖依次归类汇总，并注明各等级获奖人数，学院党委盖章。</span></p><p style="margin-bottom:13px;text-align:left;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">7</span><span style="font-size:16px;font-family:&#39;宋体&#39;">、综合测评所有报表</span><span style="font-size:16px;font-family:&#39;宋体&#39;">(</span><span style="font-size:16px;font-family:&#39;宋体&#39;">见附件</span><span style="font-size:16px;font-family:&#39;宋体&#39;">)</span><span style="font-size:16px;font-family:&#39;宋体&#39;">均由各年级到学院网页上下载打印、复印。</span></p><p style="margin-bottom:13px;text-align:left;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">8</span><span style="font-size:16px;font-family:&#39;宋体&#39;">、各个年级全部综合测评资料审核完毕，以年级为单位报送材料，只需发送电子文档，各年级将电子文档发送至scau_sxrsy_all@163.com。报送材料及要求如下：</span></p><p style="margin-bottom:13px;text-align:left;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">（</span><span style="font-size:16px;font-family:&#39;宋体&#39;">1</span><span style="font-size:16px;font-family:&#39;宋体&#39;">）华南农业大学2014-2015学年度学生综合测评统计表</span></p><p style="margin-bottom:13px;text-align:left;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">（</span><span style="font-size:16px;font-family:&#39;宋体&#39;">2</span><span style="font-size:16px;font-family:&#39;宋体&#39;">）华南农业大学2014-2015学年度“创优”呈批表；</span></p><p style="margin-bottom:13px;text-align:left;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">（</span><span style="font-size:16px;font-family:&#39;宋体&#39;">3</span><span style="font-size:16px;font-family:&#39;宋体&#39;">）华南农业大学2014-2015学年度单项活动奖学金呈批表</span><span style="font-size:16px;font-family:&#39;宋体&#39;">；</span></p><p style="margin-bottom:13px;text-align:left;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">（</span><span style="font-size:16px;font-family:&#39;宋体&#39;">4</span><span style="font-size:16px;font-family:&#39;宋体&#39;">）华南农业大学2014-2015学年度先进班集体呈批表</span><span style="font-size:16px;font-family:&#39;宋体&#39;">；</span></p><p style="margin-bottom:13px;text-align:left;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">（</span><span style="font-size:16px;font-family:&#39;宋体&#39;">5</span><span style="font-size:16px;font-family:&#39;宋体&#39;">）数学与信息学院&nbsp;软件学院2014—2015学年度“创优”活动分类统计表</span><span style="font-size:16px;font-family:&#39;宋体&#39;">&nbsp;&nbsp;和&nbsp;&nbsp;&nbsp;“创优”活动学院年级分布统计表。</span></p><p style="margin-bottom:13px;text-align:left;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">（6）</span><span style="font-size:16px;font-family:&#39;宋体&#39;">申报一等奖学金和单项奖的学生，申报先进班集体奖的班级</span><span style="font-size:16px;font-family:&#39;宋体&#39;">均</span><span style="font-size:16px;font-family:&#39;宋体&#39;">须提交2000字以上的先进事迹材料（</span><span style="font-size:16px;font-family:&#39;宋体&#39;">段落格式、字体大小，结构等严格</span><span style="font-size:16px;font-family:&#39;宋体&#39;">参</span><span style="font-size:16px;font-family:&#39;宋体&#39;">照</span><span style="font-size:16px;font-family:&#39;宋体&#39;">附件</span><span style="font-size:16px;font-family:&#39;宋体&#39;">中的参考模板</span><span style="font-size:16px;font-family:&#39;宋体&#39;">）。同时，申报一等奖学金和单项奖的学生须提交1张电子版个人近期生活照；申报先进班集体奖的班级须提交6-8张电子版班级活动照、班委会工作手册。</span></p><p style="margin-bottom:13px;text-align:left;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">（7）</span><span style="font-size:16px;font-family:&#39;宋体&#39;">数学与信息</span><span style="font-size:16px;font-family:&#39;宋体&#39;">学院</span><span style="font-size:16px;font-family:&#39;宋体&#39;">&nbsp;软件学院</span><span style="font-size:16px;font-family:&#39;宋体&#39;">2014-2015学年度“创优”拟获奖名单（参见附件</span><span style="font-size:16px;font-family:&#39;宋体&#39;">9</span><span style="font-size:16px;font-family:&#39;宋体&#39;">格式）。请按一二三等奖、单项奖和先进班集体奖分类，每类注明获奖数。文档的字体、格式要求为：四号宋体，A4纸，页边距为上30MM下17MM左右25MM，一页纸放8列名单，名单列宽为2个空格，上下对齐即单名中间要打两个空格与双名对齐，行距为固定值21磅。</span></p><p style="margin-bottom:13px;text-align:left;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">（</span><span style="font-size:16px;font-family:&#39;宋体&#39;">8</span><span style="font-size:16px;font-family:&#39;宋体&#39;">）华南农业大学学生“创优”活动总结鉴定表（综合测评结果公示完毕后，由学生处提供）</span></p><p style="margin-bottom:13px;text-align:left;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">&nbsp;</span></p><p style="margin-bottom:13px;text-align:left;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">&nbsp;</span></p><p style="margin-bottom:13px;text-align:left;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">详细工作要求及注意事项见附件。</span></p><p style="margin-bottom:13px;text-align:left;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">综合测评有关事宜的解释权归</span><span style="font-size:16px;font-family:&#39;宋体&#39;">数学与</span><span style="font-size:16px;font-family:&#39;宋体&#39;">信息学院、软件学院学院综合测评小组</span></p><p style="margin-bottom:13px;text-align:left;margin-top:0"><span style="font-size:16px;font-family:&#39;宋体&#39;">&nbsp;</span></p><p><span style="font-size:16px;font-family:&#39;宋体&#39;">咨询电话：</span><span style="font-size:16px;font-family:&#39;宋体&#39;">&nbsp;020</span><span style="font-size:16px;font-family:&#39;宋体&#39;">—</span><span style="font-size:16px;font-family:&#39;宋体&#39;">38295704</span></p><p><br/></p>', 16),
(6, '关于做好我院2015年培英工程贫困大学生助学金', 1464853968, '思源宣传部', '<p style="text-align:justify;margin-left:0pt;text-indent:0pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;">2015级本科新生：</span></p><p style="text-align:justify;margin-left:0pt;text-indent:32pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;">由</span><span style="font-size:16pt;font-family:仿宋_GB2312;">广东省青少年发展基金会联合广东省福利彩票发行中心共同发起“培英工程</span><span style="font-size:16pt;font-family:仿宋_GB2312;">—</span><span style="font-size:16pt;font-family:仿宋_GB2312;">福彩爱心助学子”项目</span><span style="font-size:16pt;font-family:仿宋_GB2312;">今年在我校开展资助。根据资助方的要求，结合学校实际，为做好我校申请工作，现将有关事宜通知如下：</span></p><p style="text-align:justify;margin-left:0pt;text-indent:32pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;font-weight:bold;">一、资助对象</span></p><p style="text-align:justify;margin-left:0pt;text-indent:32pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;">我</span><span style="font-size:16pt;font-family:仿宋_GB2312;">院</span><span style="font-size:16pt;font-family:仿宋_GB2312;">201</span><span style="font-size:16pt;font-family:仿宋_GB2312;">5</span><span style="font-size:16pt;font-family:仿宋_GB2312;">年广东省户籍家庭经济</span><span style="font-size:16pt;font-family:仿宋_GB2312;color:#ff0000;">特别</span><span style="font-size:16pt;font-family:仿宋_GB2312;color:#ff0000;">困难</span><span style="font-size:16pt;font-family:仿宋_GB2312;">本科新生。</span></p><p style="text-align:justify;margin-left:0pt;text-indent:0pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size:16pt;font-family:仿宋_GB2312;font-weight:bold;">二、资助标准与人数</span></p><p style="text-align:justify;margin-left:0pt;text-indent:0pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;">&nbsp;&nbsp;&nbsp;&nbsp;5000元/人，一次性资助。我院有3个名额。</span></p><p style="text-align:justify;margin-left:0pt;text-indent:32pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;font-weight:bold;">三、申请时间&nbsp;&nbsp;&nbsp;</span></p><p style="text-align:justify;margin-left:0pt;text-indent:0pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;">&nbsp;&nbsp;&nbsp;&nbsp;学生申请时间为2015年9月14日至17日。</span></p><p style="text-align:justify;margin-left:0pt;text-indent:0pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;">&nbsp;&nbsp;&nbsp;</span><span style="font-size:16pt;font-family:仿宋_GB2312;font-weight:bold;">&nbsp;四、申请条件</span></p><p style="text-align:justify;margin-left:0pt;text-indent:0pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;">&nbsp;&nbsp;&nbsp;&nbsp;1.</span><span style="font-size:16pt;font-family:仿宋_GB2312;">孤儿或单亲家庭</span><span style="font-size:16pt;font-family:仿宋_GB2312;">学生</span><span style="font-size:16pt;font-family:仿宋_GB2312;">；</span></p><p style="text-align:justify;margin-left:0pt;text-indent:0pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;">&nbsp;&nbsp;&nbsp;&nbsp;2.</span><span style="font-size:16pt;font-family:仿宋_GB2312;">父母双亲患重病、或残疾、或年迈、或失去劳动能力，家庭经济收入微薄，无其它亲友资助者；</span></p><p style="text-align:justify;margin-left:0pt;text-indent:0pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;">&nbsp;&nbsp;&nbsp;&nbsp;3.</span><span style="font-size:16pt;font-family:仿宋_GB2312;">父母双方下岗且收入低微者；</span></p><p style="text-align:justify;margin-left:0pt;text-indent:0pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;">&nbsp;&nbsp;&nbsp;&nbsp;4.</span><span style="font-size:16pt;font-family:仿宋_GB2312;">家庭被当地民政部门列为特困户者；</span></p><p style="text-align:justify;margin-left:0pt;text-indent:0pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;">&nbsp;&nbsp;&nbsp;&nbsp;5.</span><span style="font-size:16pt;font-family:仿宋_GB2312;">家庭人均月收入低于广州市政府当年公布的城镇居民最低生活保障者；</span></p><p style="text-align:justify;margin-left:0pt;text-indent:32pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;">6.</span><span style="font-size:16pt;font-family:仿宋_GB2312;">大学阶段暂未接受其</span><span style="font-size:16pt;font-family:仿宋_GB2312;">它</span><span style="font-size:16pt;font-family:仿宋_GB2312;">助学金资助。</span></p><p style="text-align:justify;"><span style="font-size:16pt;font-family:仿宋_GB2312;font-weight:bold;">&nbsp;&nbsp;&nbsp;&nbsp;五、申请程序</span></p><p style="text-align:justify;margin-left:0pt;text-indent:32pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;">1．</span><span style="font-size:16pt;font-family:仿宋_GB2312;">学生</span><span style="font-size:16pt;font-family:仿宋_GB2312;">申请。符合条件的</span><span style="font-size:16pt;font-family:仿宋_GB2312;">学生，准备纸质版申请书（</span><span style="font-size:16pt;font-family:仿宋_GB2312;">一式一份，800字以上，统一使用有学校抬头的信签纸手写，注意书写工整</span><span style="font-size:16pt;font-family:仿宋_GB2312;">），</span><span style="font-size:16pt;font-family:仿宋_GB2312;">《培英工程助学金申请表》</span><span style="font-size:16pt;font-family:仿宋_GB2312;">（附件</span><span style="font-size:16pt;font-family:仿宋_GB2312;">2，</span><span style="font-size:16pt;font-family:仿宋_GB2312;">一式</span><span style="font-size:16pt;font-family:仿宋_GB2312;color:#ff0000;">三</span><span style="font-size:16pt;font-family:仿宋_GB2312;">份，以下简称申请表，</span><span style="font-size:16pt;font-family:仿宋_GB2312;color:#ff0000;">手动填写，不能打印</span><span style="font-size:16pt;font-family:仿宋_GB2312;">），《华南农业大学</span><span style="font-size:16pt;font-family:仿宋_GB2312;">2015年培英工程助学金申报学生信息汇总表》（附件3，一式一份，以下简称汇总表），</span><span style="font-size:16pt;font-family:仿宋_GB2312;">学生家庭所在街道或镇以上民政部门开具的家庭经济状况证明</span><span style="font-size:16pt;font-family:仿宋_GB2312;">和申请条件中有关证明</span><span style="font-size:16pt;font-family:仿宋_GB2312;">复印件</span><span style="font-size:16pt;font-family:仿宋_GB2312;">（以上均一式一份）、工行卡复印件（可等到名单公示期过后再补交到年级辅导员），</span><span style="font-size:16pt;font-family:仿宋_GB2312;">向所在学院提出申请。</span></p><p style="text-align:justify;margin-left:0pt;text-indent:0pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size:16pt;font-family:仿宋_GB2312;">2．学院组织召开专项评审会议，对本学院申请学生进行评选和审核，将评审结果在学院范围内公示3日。</span></p><p style="text-align:justify;margin-left:0pt;text-indent:0pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size:16pt;font-family:仿宋_GB2312;">3．学校对各学院上报名单进行审核，对符合申请条件并通过审核的拟资助学生</span><span style="font-size:16pt;font-family:仿宋_GB2312;">名单</span><span style="font-size:16pt;font-family:仿宋_GB2312;">在全校范围内公示5个工作日。公示无异议，将有关材料报</span><span style="font-size:16pt;font-family:仿宋_GB2312;">广东省青少年发展基金会</span><span style="font-size:16pt;font-family:仿宋_GB2312;">，</span><span style="font-size:16pt;font-family:仿宋_GB2312;">资助方</span><span style="font-size:16pt;font-family:仿宋_GB2312;">按规定程序和标准给予资助。&nbsp;</span></p><p style="text-align:justify;margin-left:0pt;text-indent:32pt;">&nbsp;</p><p style="text-align:justify;margin-left:0pt;text-indent:32pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;font-weight:bold;">六、工作要求&nbsp;&nbsp;&nbsp;</span></p><p style="text-align:justify;margin-left:0pt;text-indent:0pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;">&nbsp;&nbsp;&nbsp;&nbsp;1.</span><span style="font-size:16pt;font-family:仿宋_GB2312;">请</span><span style="font-size:16pt;font-family:仿宋_GB2312;">新生年级</span><span style="font-size:16pt;font-family:仿宋_GB2312;">高度重视，</span><span style="font-size:16pt;font-family:仿宋_GB2312;">坚持</span><span style="font-size:16pt;font-family:仿宋_GB2312;">公平、公开、公正的原则，</span><span style="font-size:16pt;font-family:仿宋_GB2312;">在规定时间内完成评选</span><span style="font-size:16pt;font-family:仿宋_GB2312;">工作。</span></p><p style="text-align:justify;margin-left:0pt;text-indent:0pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;">&nbsp;&nbsp;&nbsp;&nbsp;2.</span><span style="font-size:16pt;font-family:仿宋_GB2312;">新生年级</span><span style="font-size:16pt;font-family:仿宋_GB2312;">2015-2016学年度家庭经济困难学生认定工作如没有完成，请根据《关于做好我校2015-2016学年度家庭经济困难学生认定工作的通知》有关要求，对申请的学生可以先行进行认定，便于本次评选工作开展。</span></p><p style="text-align:justify;margin-left:0pt;text-indent:0pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;">&nbsp;&nbsp;&nbsp;&nbsp;3</span><span style="font-size:16pt;font-family:仿宋_GB2312;">.</span><span style="font-size:16pt;font-family:仿宋_GB2312;">申请学生自愿接受</span><span style="font-size:16pt;font-family:仿宋_GB2312;">资助</span><span style="font-size:16pt;font-family:仿宋_GB2312;">的同时，</span><span style="font-size:16pt;font-family:仿宋_GB2312;">根据资助方要求</span><span style="font-size:16pt;font-family:仿宋_GB2312;">，</span><span style="font-size:16pt;font-family:仿宋_GB2312;">应</span><span style="font-size:16pt;font-family:仿宋_GB2312;">积极参加公益活动，践行“受惠社会，回报社会”的公益精神。受助学生如出现有违</span><span style="font-size:16pt;font-family:仿宋_GB2312;">资助方</span><span style="font-size:16pt;font-family:仿宋_GB2312;">要求的情况，将取消已获得</span><span style="font-size:16pt;font-family:仿宋_GB2312;">资助</span><span style="font-size:16pt;font-family:仿宋_GB2312;">的资格，</span><span style="font-size:16pt;font-family:仿宋_GB2312;">并</span><span style="font-size:16pt;font-family:仿宋_GB2312;">追回助学金。</span></p><p style="text-align:justify;margin-left:0pt;text-indent:32pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;">4.请申请学生将《</span><span style="font-size:16pt;font-family:仿宋_GB2312;">培英工程助学金申请表</span><span style="font-size:16pt;font-family:仿宋_GB2312;">》用A4纸</span><span style="font-size:16pt;font-family:仿宋_GB2312;color:#ff0000;">单面</span><span style="font-size:16pt;font-family:仿宋_GB2312;">打印，根据申请表填写要求和</span><span style="font-size:16pt;font-family:仿宋_GB2312;">样式</span><span style="font-size:16pt;font-family:仿宋_GB2312;">（附件</span><span style="font-size:16pt;font-family:仿宋_GB2312;">5</span><span style="font-size:16pt;font-family:仿宋_GB2312;">），手动填写</span><span style="font-size:16pt;font-family:仿宋_GB2312;">；填写时</span><span style="font-size:16pt;font-family:仿宋_GB2312;color:#ff0000;">字迹清晰、字面干净整洁、规范，不能随意涂改</span><span style="font-size:16pt;font-family:仿宋_GB2312;">；</span><span style="font-size:16pt;font-family:仿宋_GB2312;">请申请学生</span><span style="font-size:16pt;font-family:仿宋_GB2312;">准备</span><span style="font-size:16pt;font-family:仿宋_GB2312;color:#ff0000;">近期一寸彩色免冠照三张</span><span style="font-size:16pt;font-family:仿宋_GB2312;">，用于张贴在申请表中；由于资助方将通过工商银行发放助学金，请申请学生准备好用本人真实姓名开户的工商银行账户；每个学生申请材料按申请书、</span><span style="font-size:16pt;font-family:仿宋_GB2312;">申请表</span><span style="font-size:16pt;font-family:仿宋_GB2312;">、有关证明材料复印件和工行卡复印件顺序依次排列，并使用回形针夹住左侧。</span></p><p style="text-align:justify;margin-left:0pt;text-indent:32pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;">5.学校将通过学生工作部（处）和自强社微信平台及时发布评选、资助金发放等情况，请申请同学及时加入两个微信公众号。</span></p><p style="text-align:justify;margin-left:0pt;text-indent:0pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;">&nbsp;&nbsp;咨询电话：38295704&nbsp;刘老师&nbsp;倪老师&nbsp;</span></p><p style="text-align:justify;margin-left:0pt;text-indent:0pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;数学与信息学院&nbsp;软件学院</span></p><p style="text-align:justify;margin-left:0pt;text-indent:0pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2015年9月14日</span></p><p><br/></p>', 24),
(7, '关于我院2015年培英工程贫困大学生助学金受助学生的推荐名单公示', 1464854008, '思源宣传部', '<p><span style="font-size:18px;font-family:&#39;宋体&#39;;font-weight:normal;color:#555555;">各年级各班：</span></p><p><span style="font-size:18px;font-family:&#39;宋体&#39;;font-weight:normal;color:#555555;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;根据学校</span><span style="font-size:18px;font-family:&#39;宋体&#39;;font-weight:normal;color:#555555;">的统一安排和要求，结合学院的实际情况，依照个人申请，学院审核，我院2015年培英工程贫困大学生助学金的评选工作已经顺利完成，现予以公示。公示时间为201</span><span style="font-size:18px;font-family:&#39;宋体&#39;;font-weight:normal;color:#555555;">5</span><span style="font-size:18px;font-family:&#39;宋体&#39;;font-weight:normal;color:#555555;">年</span><span style="font-size:18px;font-family:&#39;宋体&#39;;font-weight:normal;color:#555555;">9</span><span style="font-size:18px;font-family:&#39;宋体&#39;;font-weight:normal;color:#555555;">月</span><span style="font-size:18px;font-family:&#39;宋体&#39;;font-weight:normal;color:#555555;">18</span><span style="font-size:18px;font-family:&#39;宋体&#39;;font-weight:normal;color:#555555;">日至</span><span style="font-size:18px;font-family:&#39;宋体&#39;;font-weight:normal;color:#555555;">22</span><span style="font-size:18px;font-family:&#39;宋体&#39;;font-weight:normal;color:#555555;">日。如有异议，请在公示期工作时间内通过电话或书面形式向学生工作办公室反映。反映情况时要自报或签署真实姓名，不报或不签署真实姓名的，一律不予受理。</span></p><p><span style="font-size:18px;font-family:&#39;宋体&#39;;font-weight:normal;color:#555555;">&nbsp;&nbsp;&nbsp; 公示名单如下:</span></p><p><span style="font-size:18px;font-family:&#39;宋体&#39;;font-weight:normal;color:#555555;">&nbsp;&nbsp;&nbsp; 2015级计算机科学与技术2班，201525010204，方奕婷</span></p><p><span style="font-size:18px;font-family:&#39;宋体&#39;;font-weight:normal;color:#555555;">&nbsp;&nbsp;&nbsp; 2015级计算机科学与技术3班，201525010325，张佳涛</span></p><p><span style="font-size:18px;font-family:&#39;宋体&#39;;font-weight:normal;color:#555555;">&nbsp;&nbsp;&nbsp; 2015级信息与计算科学4班，&nbsp;&nbsp;201512010404，陈志超</span></p><p><span style="font-size:18px;font-family:&#39;宋体&#39;;font-weight:normal;color:#555555;">&nbsp;&nbsp;&nbsp; </span></p><p style="text-indent:24pt;"><span style="font-size:18px;font-family:&#39;宋体&#39;;font-weight:normal;color:#555555;">联系电话:020-38295704&nbsp;&nbsp;刘老师&nbsp;倪老师</span></p><p style="text-indent:24pt;"><span style="font-size:18px;font-family:&#39;宋体&#39;;font-weight:normal;color:#555555;">数学与信息学院&nbsp;软件学院</span></p><p style="text-indent:24pt;"><span style="font-size:18px;font-family:&#39;宋体&#39;;font-weight:normal;color:#555555;">2015年9月18日</span></p><p><br/></p>', 22);
INSERT INTO `sy_article` (`id`, `subject`, `lastmodifytime`, `author`, `message`, `createtime`) VALUES
(8, '关于做好我院2015年广东省户籍家庭经济困难本科新生资助工作的通知', 1464854050, '思源宣传部', '<p style="text-align:justify;"><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">2015级各班</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">：</span></p><p style="text-align:justify;"><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">根据</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">《广东省人民政府办公厅关于做好我省贫困家庭大学新生入学资助工作的通知》</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">（粤</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">府办明电</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">〔201</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">2</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">〕</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">316</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">号）</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">、</span></p><p style="text-align:justify;"><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">《广东省家庭经济困难大学新生资助专项资金管理办法》（粤财教〔2014〕97号）和《广东省教育厅关于做好我省201</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">5</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">年家庭经济困难大学新生资助工作的通知》（粤教助函〔201</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">5</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">〕</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">39</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">号），</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">为切实帮助我校</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">广东省户籍家庭经济困难本科</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;"><span style="font-size:18px;color:#e53333;">新生</span>，落实广东省</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">家庭经济困难新生资助</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">政策，现就做好有关</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">工作通知如下：</span></p><p style="text-align:justify;"><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">一、资助对象</span></p><p style="text-align:justify;"><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">我</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">院</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">201</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">5</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">年广东省户籍家庭经济困难本科新生。</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;</span></p><p style="text-align:justify;"><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">二</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">、资助人数和标准</span></p><p style="text-align:justify;text-indent:32pt;"><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">1．</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">受资助名额为</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">94</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">人。</span></p><p style="text-align:justify;text-indent:32pt;"><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">2．</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">标准为</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">5000元/人。</span></p><p style="text-align:justify;"><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">三、申请时间</span></p><p style="text-align:justify;"><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp;学生申请时间为</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">2015年9月18日到21日</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">。</span></p><p style="text-align:justify;"><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">四</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">、申请条件</span></p><p style="text-align:justify;"><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">家庭经济困难，本人及其家庭所能筹集到的资金不足以缴纳第一学年学费和支付生活费用的广东省户籍</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">我校</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">2015级</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">本科新生，特别是其中的孤残学生、烈士子女、优抚家庭子女、城乡低保家庭子女或低收入家庭子女（请提供有关证明资料），可以申请新生资助。</span></p><p style="text-align:justify;"><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">五</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">、申请程序</span></p><p style="text-align:justify;text-indent:32pt;"><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">1</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">.</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">学生</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">申请。符合条件的</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">学生，在学院规定的时间内，准备纸质版《申请书》（</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">一式一份，800字以上，统一使用有学校抬头的信签纸手写，内容包括家</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:normal;">庭情况、申请理由</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:normal;">等</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">）、</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">《广东省家庭经济困难大学新生资助专项资金申请表》（附件2</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;，以下简称《申请表》，一式两份，手写打印都可</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">）</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">、学生</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">个人身份证</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">和</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">学生家庭所在街道或镇以上民政部门开具的家庭经济状况证明复印件</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">（如是</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">孤残学生、烈士子女、优抚家庭子女、城乡低保家庭子女或低收入家庭子女</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">，请提供有关证明材料复印件，以上均一式一份）、</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">《</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">华南农业大学201</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">5</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">年广东省家庭经济困难本科新生资助情况表</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">》（附件</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">3，以下简称《情况表》，一式一份</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">）</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">；电子版《</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">情况表</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">》(由总助班汇总后发给年级辅导员)，</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">向所在学院提出申请。</span></p><p style="text-align:justify;text-indent:32pt;"><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">2</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">.</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">学院初审与公示。各学院组织召开专项评审会议，对本学院申请学生进行评选和审核，将评审结果在学院范围内公示3日。公示无异议，报学生工作部（处）。</span></p><p style="text-align:justify;text-indent:32pt;"><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">评审结束后，拟受助学生需要在学校奖助学系统（http://jzx.scaustu.com）填报有关信息。学院在学校</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">奖助学系统</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">完成受助学生信息审核。</span></p><p style="text-align:justify;"><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp; &nbsp;</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">3</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">.</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">学校审核与公示。学校对各学院上报名单进行审核，对符合申请条件并通过审核的拟</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">受</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">助学生</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">名单</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">在全校范围内公示5日。公示无异议，将有关材料报省教育厅，按规定程序和标准给予资助。&nbsp;</span></p><p style="text-align:justify;"><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp; </span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">六、其他</span></p><p style="text-align:justify;text-indent:32pt;"><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">1.</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">申请学生</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">应</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">积极参加公益活动</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">，</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">受助学生如出现有</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">违反有关文件</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">要求</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">时</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">，将取消</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">受助</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">资格，</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">并</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">追回助学金。</span></p><p style="text-align:justify;text-indent:32pt;"><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">2.已经在生源地申请了“广东省少数民族聚集区少数民族大学生资助”等由政府财政资助的学生，原则上不重复享受本次资助。同时，获得本次资助的学生，在同一时间段内，原则上不再享受省财政设立的其它专项资助。</span></p><p style="text-align:justify;text-indent:32pt;"><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">3.《</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">申请表</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">》限用A4纸双面打印，请申请学生根据《</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">申请表</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">》填写要求和</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">样式</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">，电脑填写打印</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">；手动填写处应</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">字迹清晰、字面干净整洁、规范，不能随意涂改</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">。</span></p><p style="text-align:justify;text-indent:32pt;"><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">4.</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">每个学生上交材料务必将申请材料将《申请书》、《</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">申请表</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">》、学生</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">个人身份证</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">和</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">学生家庭所在街道或镇以上民政部门开具的家庭经济状况证明复印件</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">依次排列，并使用回形针夹住左侧。</span></p><p style="text-align:justify;">&nbsp;</p><p style="text-align:justify;"><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">数学与信息学院</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;软件学院</span></p><p style="text-align:justify;"><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2015</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">年</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">9</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">月</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">18</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">日</span></p><p><br/></p>', 19),
(9, '关于推荐我院新长城自强助学金候选学生的通知', 1464854095, '思源宣传部', '<p><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">2013级、2015级各班：</span></p><p><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">为了帮助</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">家庭经济困难学生</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">顺利完成大学学业，中国扶贫基金会于2002年9月1日发起“新长城—特困大学生自强项目”</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">，该项目已连续多年对我校开展资助。根据今年</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">中国扶贫基金会</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">推荐要求，结合学校实际，现就做好有关工作通知如下：</span></p><p><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">一、资助对象</span></p><p style="text-indent:32pt;"><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">根据中国扶贫基金会的要求，资助对象仅限林学与风景园林学院（</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">园林</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">专业和</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">城乡规划</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">专业</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">）、水利与土木工程学院（</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">土木工程</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">专业和</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">建筑学</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">专业</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">），数学与信息学院、软件学院（</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">数学与应用数学</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">专业和</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">计算机类</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">专业</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">）2015级家庭经济困难本科新生。</span></p><p style="text-indent:32pt;"><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">二、资助人数和标准</span></p><p><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">1</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">.我院2015级名额共13名；2013级因有一名学生（原获得新长城自强助学金的受助资格）现已脱贫，经资助方研究决定，特在我院2013级范围内增补一个名额</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">。</span></p><p><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">2</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">.</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">资助标准为3000元</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">/人/年</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">，</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">连续资助</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">4年</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">。</span></p><p><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">三、申请</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">时间</span></p><p><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp;学生申请时间为2015年9月18日到21日。</span></p><p><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">四</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">、申请条件</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">和要求</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">&nbsp;</span></p><p><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">1</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">.农村生源的家庭经济困难学生，其中</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;color:#ff0000;">女生</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">优先；</span></p><p><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp;2.申请学生要自强拼搏，积极进取。</span></p><p><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp; </span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">五</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">、申请程序</span></p><p style="text-align:justify;"><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">1</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">.</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">学生</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">申请。符合条件的2015级</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">学生，在学院规定的时间内，准备纸质版《申请书》（</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">一式一份，800字以上，统一使用有学校抬头的信签纸手写,内容包括</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:normal;">家庭情况、申请理由、对新长城的理解及自己的人生理想</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:normal;">等</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">）、</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">《</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">新长城自强助学金候选学生申请表</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">》（附件</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">2&nbsp;，以下简称《申请表》，一式两份</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">）</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">、</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">《</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">新长城项目候选学生一览表》（附件</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">3，以下简称《一览表》，一式一份</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">）、</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">学生家庭所在街道或镇以上民政部门开具的家庭经济状况证明复印件</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">（一式一份）</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">；电子版《</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">一览</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">表</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">》（由总助班汇总后发到年级辅导员），</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">向所在学院提出申请。</span></p><p style="text-align:justify;"><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp; 符合条件的2013级<span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">学生，在学院规定的时间内，准备纸质版《申请书》（<span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">一式一份，800字以上，统一使用有学校抬头的信签纸手写,内容包括</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:normal;">家庭情况、申请理由、对新长城的理解及自己的人生理想</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:normal;">等</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">）、<span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">学生家庭所在街道或镇以上民政部门开具的家庭经济状况证明复印件</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">（一式一份），在21日前以班级为单位交到年级辅导员处，过期不候。</span></span></span></span></p><p style="text-align:justify;"><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp;2.</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">学院初审与公示。各学院组织召开专项评审会议，对本学院申请学生进行评选和</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">推荐</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">，将评审结果在学院范围内公示3日。公示无异议，报学生工作部（处）。</span></p><p style="text-align:justify;"><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">评审结束后，拟受助学生需要在学校</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">奖助学系统（</span><img src="http://scausy.cn/scausy/Public/ueditor/themes/default/images/spacer.gif" word_img="file:///C:/Users/Administrator/AppData/Local/Temp/ksohtml/wps_clip_image-26223.png" style="background:url(http://scausy.cn/scausy/Public/ueditor/themes/default/images/word.gif) no-repeat center center;border:1px solid #ddd" height="15" width="20"/><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;"><a href="http://jzx.scaustu.com">http://jzx.scaustu.com</a>）</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">填报有关信息，学院在学校</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">奖助学系统</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">完成受助学生信息审核。</span></p><p style="text-align:justify;"><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp; </span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">3.</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">学校审核与公示。学校对各学院</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">推荐</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">名单进行审核，对符合申请条件并通过审核的拟</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">受</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">助学生</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">名单</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">在全校范围内公示5日。公示无异议，将有关材料</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">报送中国扶贫基金会。</span></p><p><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp;4.中国扶贫基金会审核。审核通过后将</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">按规定程序和标准给予资助。&nbsp;</span></p><p><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">&nbsp; &nbsp;</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">六</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">、</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">其他</span></p><p><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp;1.受助期间，学生家庭经济状况出现明显好转的，请及时向学院和学校反映，并主动申请退出资助，将受助机会让给其它更需要帮助的学生。</span></p><p><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp;2.</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">受助</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">学生</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">应</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">积极参加中国扶贫基金会和学校自强社开展的有关公益活动</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">；</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">受助学生如出现有</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">违反有关文件</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">要求</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">时</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">，将取消</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">受助</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">资格，</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">并</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">追回助学金。</span></p><p><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp; </span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">3.《</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">申请表</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">》限用A4纸双面打印，请申请学生根据《</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">申请表</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">》填写要求和</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">样式</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">，手动填写</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">；填写时应</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">字迹清晰、字面干净整洁、规范，不能随意涂改</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">。</span></p><p><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp; </span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">4.</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">每个申请助学金的学生务必将《申请书》、《</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">申请表</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">》、</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">《</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">一览</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">表</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">》、</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">学生家庭所在街道或镇以上民政部门开具的家庭经济状况证明复印件</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">依次排列，并使用回形针夹住左侧。</span></p><p>&nbsp;</p><p><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">数学与信息学院</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;软件学院</span></p><p><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;color:#ff0000;">&nbsp;&nbsp; &nbsp;&nbsp;</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">2015</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">年</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">9</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">月</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">18</span><span style="font-size:18px;font-family:&#39;仿宋_GB2312&#39;;">日</span></p><p><br/></p>', 26),
(10, '关于我院新长城自强助学金推荐受助学生的名单公示', 1464854153, '思源宣传部', '<p><span style="font-size:18px;font-family:宋体;color:#555555;"><span style="font-size:18px;font-family:宋体;"><p><span style="font-size:18px;font-family:宋体;color:#555555;">各年级各班：</span></p><p><span style="font-size:18px;font-family:Verdana;color:#555555;">&nbsp;&nbsp;</span><span style="font-size:18px;font-family:宋体;color:#555555;">&nbsp;</span><span style="font-size:18px;font-family:Verdana;color:#555555;">&nbsp;&nbsp;<span style="font-size:18px;font-family:宋体;">根据学校要求，我院新长城自强助学金受助</span></span><span style="font-size:18px;font-family:宋体;color:#555555;">学生资格</span><span style="font-size:18px;font-family:Verdana;color:#555555;">的评选工作已经顺利完成，</span><span style="font-size:18px;font-family:宋体;color:#555555;">按照学生个人申请，班级审核，学院审核</span><span style="font-size:18px;font-family:Verdana;color:#555555;">，现将我院推荐的学生名单</span><span style="font-size:18px;font-family:Verdana;color:#555555;">（如下）</span><span style="font-size:18px;font-family:宋体;color:#555555;">进行公示，公示期为<span style="font-size:18px;font-family:Verdana;">9</span><span style="font-size:18px;font-family:宋体;">月</span><span style="font-size:18px;font-family:Verdana;">22</span><span style="font-size:18px;font-family:宋体;">日—</span><span style="font-size:18px;font-family:Verdana;">24</span><span style="font-size:18px;font-family:宋体;">日，如对公示的结果有异议，请在公示时间内以来电或来信的方式向年级辅导员倪老师或刘老师反映。来电或者来信必须告知真实姓名，否则不予受理。</span></span></p><p><br/></p><table class="ke-zeroborder" width="241"><tbody><tr class="firstRow"><td valign="middle" width="46"><p style="vertical-align:middle;text-align:left;"><span style="font-size:16px;font-family:宋体;color:#000000;font-style:normal;">年级</span></p></td><td valign="middle" width="99"><p style="vertical-align:middle;text-align:left;"><span style="font-size:16px;font-family:宋体;color:#000000;font-style:normal;">专业班级</span></p></td><td valign="middle" width="104"><p style="vertical-align:middle;text-align:left;"><span style="font-size:16px;font-family:宋体;color:#00000