INSERT INTO `sy_article` (`id`, `subject`, `lastmodifytime`, `author`, `message`, `createtime`) VALUES
(11, '关于我院2015年广东省户籍家庭经济困难本科新生资助推荐名单的公示', 1464854221, '思源宣传部', '<p><span style="font-size:18px;font-family:宋体;color:#555555;">2015</span><span style="font-size:18px;font-family:宋体;color:#555555;">级各班：</span></p><p><span style="font-size:18px;font-family:Verdana;color:#555555;">&nbsp;&nbsp;</span><span style="font-size:18px;font-family:宋体;color:#555555;">&nbsp;</span><span style="font-size:18px;font-family:Verdana;color:#555555;">&nbsp;&nbsp;<span style="font-size:18px;font-family:宋体;">根据学校要求，我院</span><span style="font-size:18px;font-family:Verdana;">2015</span><span style="font-size:18px;font-family:宋体;">年广东省户籍家庭经济困难本科新生资助的评选工作已经顺利完成，</span></span><span style="font-size:18px;font-family:宋体;color:#555555;">按照学生个人申请，班级审核，学院审核</span><span style="font-size:18px;font-family:Verdana;color:#555555;">，现将我院推荐的学生名单</span><span style="font-size:18px;font-family:Verdana;color:#555555;">（</span><span style="font-size:18px;font-family:宋体;color:#555555;">见附件</span><span style="font-size:18px;font-family:Verdana;color:#555555;">）</span><span style="font-size:18px;font-family:宋体;color:#555555;">进行公示，公示期为<span style="font-size:18px;font-family:Verdana;">9</span><span style="font-size:18px;font-family:宋体;">月</span><span style="font-size:18px;font-family:Verdana;">22</span><span style="font-size:18px;font-family:宋体;">日—</span><span style="font-size:18px;font-family:Verdana;">24</span><span style="font-size:18px;font-family:宋体;">日</span></span><span style="font-size:18px;font-family:Verdana;color:#555555;">。如对公示的结果有异议，请在公示时间内以来电或来信的方式向年级辅导员倪老师或刘老师反映。来电或者来信必须告知真实姓名，否则不予受理。</span></p><p style="text-indent:13.5pt;"><span style="font-size:18px;font-family:Verdana;color:#555555;">咨询电话：<span style="font-size:18px;font-family:Verdana;">38295704&nbsp;</span><span style="font-size:18px;font-family:宋体;">刘老师</span></span><span style="font-size:18px;font-family:宋体;color:#555555;">&nbsp;倪老师</span></p><p style="text-indent:13.5pt;"><span style="font-size:18px;font-family:宋体;color:#555555;">数学与信息学院&nbsp;软件学院</span></p><p style="text-indent:13.5pt;"><span style="font-size:18px;font-family:宋体;color:#555555;">&nbsp;2015<span style="font-size:18px;font-family:宋体;">年</span><span style="font-size:18px;font-family:Verdana;">9</span><span style="font-size:18px;font-family:宋体;">月</span><span style="font-size:18px;font-family:Verdana;">22</span><span style="font-size:18px;font-family:宋体;">日</span></span></p><p><br/></p>', 23),
(12, '关于我院获得2015-2016学年度高校助学贷款资格的学生名单公示', 1464854278, '思源宣传部', '<p><span style="font-size:18px;font-family:宋体;font-weight:normal">各年级各班：</span></p><p style="text-indent:37px"><span style="font-size:18px;font-family:宋体;font-weight:normal">按照学校的统一安排，学生个人在规定的时间内提出纸质申请和网上申请，经过年级审核和学院复核，我院<span style="font-size:18px;font-family:&#39;Times New Roman&#39;">2015-2016</span><span style="font-size:18px;font-family:宋体">学年度高校助学贷款资格的审核工作已经顺利完成。遵照国家开发银行的最新相关规定和要求，必须对获得</span><span style="font-size:18px;font-family:&#39;Times New Roman&#39;">2015-2016</span><span style="font-size:18px;font-family:宋体">学年度高校助学贷款的学生名单进行公示。现将我院获得</span><span style="font-size:18px;font-family:&#39;Times New Roman&#39;">2015-2016</span><span style="font-size:18px;font-family:宋体">学年度高校助学贷款资格的学生名单进行公示，公示时间为</span><span style="font-size:18px;font-family:&#39;Times New Roman&#39;">9</span><span style="font-size:18px;font-family:宋体">月</span><span style="font-size:18px;font-family:&#39;Times New Roman&#39;">28</span><span style="font-size:18px;font-family:宋体">日—</span><span style="font-size:18px;font-family:&#39;Times New Roman&#39;">29</span><span style="font-size:18px;font-family:宋体">日。</span></span></p><p style="text-indent:37px"><span style="font-size:18px;font-family:宋体;font-weight:normal">咨询电话：<span style="font-size:18px;font-family:&#39;Times New Roman&#39;">38295704</span></span></p><p style="text-indent:37px"><span style="font-size:18px;font-family:宋体;font-weight:normal">数学与信息学院&nbsp;软件学院</span></p><p><span style="font-size:18px;font-family:宋体;font-weight:normal">2015<span style="font-size:18px;font-family:宋体">年</span><span style="font-size:18px;font-family:&#39;Times New Roman&#39;">9</span><span style="font-size:18px;font-family:宋体">月</span><span style="font-size:18px;font-family:&#39;Times New Roman&#39;">28</span><span style="font-size:18px;font-family:宋体">日</span></span></p><p><br/></p>', 30),
(13, '关于2015-2016学年度我院家庭经济困难学生认定结果公示', 1464854313, '思源宣传部', '<p style="text-align:left;"><span style="font-size:18px;font-family:宋体;color:#555555;">各年级各班：</span></p><p style="text-align:left;text-indent:24pt;"><span style="font-size:18px;font-family:宋体;color:#555555;">根据《华南农业大学家庭经济困难学生认定与管理办法（试行）》华南农办</span><span style="font-size:18px;font-family:Verdana;color:#555555;">[2008]79</span><span style="font-size:18px;font-family:宋体;color:#555555;">号，经学生申请，班级初审，学院复审，我院开展的<span style="font-size:18px;font-family:&#39;Times New Roman&#39;;">2015-2016</span><span style="font-size:18px;font-family:宋体;">学年度家庭经济困难学生资格认定工作已顺利完成，现将本学年度的认定结果公示如下（名单见附件），公示期为</span><span style="font-size:18px;font-family:&#39;Times New Roman&#39;;">2014</span><span style="font-size:18px;font-family:宋体;">年</span></span><span style="font-size:18px;font-family:宋体;color:#555555;">9</span><span style="font-size:18px;font-family:宋体;color:#555555;">月</span><span style="font-size:18px;font-family:宋体;color:#555555;">2</span><span style="font-size:18px;font-family:宋体;color:#555555;">8<span style="font-size:18px;font-family:宋体;">日至</span></span><span style="font-size:18px;font-family:宋体;color:#555555;">30</span><span style="font-size:18px;font-family:宋体;color:#555555;">日下午5点前，如对公示名单和认定结果有异议，</span><span style="font-size:18px;font-family:Verdana;color:#555555;">请在公示期内以电话或书面形式向学院学生工作办公室反映。反映情况时要自报或签署真实姓名并提供相应证明材料，不报或不签署真实姓名的，一律不予受理。逾期反映者，亦不再受理。</span></p><p style="text-align:left;"><span style="font-size:18px;font-family:Verdana;color:#555555;">&nbsp;&nbsp;</span></p><p style="text-align:left;"><span style="font-size:18px;font-family:Verdana;color:#555555;">&nbsp;&nbsp;&nbsp;&nbsp;<span style="font-size:18px;font-family:宋体;">联系电话：</span><span style="font-size:18px;font-family:Verdana;">38295704</span></span></p><p style="text-align:left;"><span style="font-size:18px;font-family:Verdana;color:#555555;">&nbsp;&nbsp;&nbsp;&nbsp;<span style="font-size:18px;font-family:宋体;">联系人：</span></span><span style="font-size:18px;font-family:宋体;color:#555555;">各年级辅导员&nbsp;刘</span><span style="font-size:18px;font-family:Verdana;color:#555555;">老师</span></p><p><br/></p>', 15),
(14, '关于做好我院2014年—2015年国家奖学金评选工作的通知 ', 1464854414, '思源宣传部', '<p style="text-align:left;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">2012、2013、2014级各班</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">：</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">根\r\n据《广东省财政厅广东省教育厅关于印发&lt;广东省普通高校本专科生国家奖助学金管理办法&gt;的通知》（粤财教〔2014〕191号）、《广东省教\r\n育厅关于做好2015年我省高校本专科生国家奖助学金工作的通知》（粤教助函〔2015〕50号）和《华南农业大学本科学生国家奖助学金管理办法》\r\n（〔2014〕151号），以及根据《关于做好2015年我校本科生国家奖助学金工作的通知》，为做好今年我院国家奖学金评审工作，现将有关事项通知如\r\n下：</span></p><p style="text-align:left;text-indent:-21pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;font-weight:bold;color:#000000;">&nbsp;&nbsp;&nbsp;&nbsp;一、</span><span style="font-size:24px;font-family:&#39;Times New Roman&#39;;background:#ffffff;font-weight:bold;color:#000000;">&nbsp;&nbsp;</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;font-weight:bold;color:#000000;">国家奖学金的申请对象、标准、名额</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">（一）申请对象：国家奖学金用于奖励全日制二年级及以上（不含评选当年已毕业的学生）特别优秀的本科学生。我院2012级、2013级、2014级的学生均可申请。</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">（二）申请标准：</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">国家奖学金。8000元/人，一次性奖励。</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">（三）名额分配：我院共11个名额，按照各年级参加综合测评的实际人数情况，12级和13级各有4个名额，14级有3个名额。</span></p><p style="text-align:left;text-indent:-21pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;font-weight:bold;color:#000000;">&nbsp;&nbsp;&nbsp;&nbsp;二、</span><span style="font-size:24px;font-family:&#39;Times New Roman&#39;;background:#ffffff;font-weight:bold;color:#000000;">&nbsp;&nbsp;</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;font-weight:bold;color:#000000;">国家奖学金申请条件和要求</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">（一）具有中华人民共和国国籍。</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">（二）热爱社会主义祖国，拥护中国共产党的领导。</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">（三）遵守宪法和法律，遵守学校规章制度。</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">（四）诚实守信，道德品质优良。</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">（五）学习成绩优异，</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">2014-2015学年度学习成绩排名（此处以专业个人平均绩点排名结果为依据）与综合测评成绩（此处以年级综合测评排名结果为依据）排名</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#ff0000;">均</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">位于前10%（含）并获得学校一等奖学金。</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">（六）社会实践、创新能力、综合素质等方面特别突出，学习成绩排名或综合测评成绩排名未达到前10%，但两项成绩排名均须位于前30%，必须在道德风尚、学术研究、学科竞赛、创新发明、社会实践、社会工作、体育竞赛、文艺比赛等某一方面表现特别优秀，方可申请国家奖学金。</span></p><p style="text-align:left;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">&nbsp;&nbsp;&nbsp;&nbsp;社会实践、创新能力、综合素质等方面表现特别优秀是指学生在道德风尚、学术研究、学科竞赛、创新发明、社会实践、社会工作、体育竞赛、文艺比赛等某一方面表现特别突出。具体标准如下：</span></p><p style="text-align:left;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">&nbsp;&nbsp;&nbsp;&nbsp;1.在社会主义精神文明建设中表现突出，具有见义勇为、助人为乐、奉献爱心、服务社会、自立自强的实际行动，在本校、本地区产生重大影响，在全国产生较大影响，有助于树立良好的社会风尚。</span></p><p style="text-align:left;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">&nbsp;&nbsp;&nbsp;&nbsp;2.在学术研究上取得显著成绩，以第一作者发表的论文被SCI、EI、ISTP、SSCI全文收录，以第一、二作者出版学术专著（须通过专家鉴定）。</span></p><p style="text-align:left;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">&nbsp;&nbsp;&nbsp;&nbsp;3.在学科竞赛方面取得显著成绩，在国际和全国性专业学科竞赛、课外学术科技竞赛等竞赛中获一等奖（或金奖）及以上奖励。</span></p><p style="text-align:left;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">&nbsp;&nbsp;&nbsp;&nbsp;4.在创新发明方面取得显著成绩，科研成果获省、部级以上奖励或获得国家专利（须通过专家鉴定）。</span></p><p style="text-align:left;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">&nbsp;&nbsp;&nbsp;&nbsp;5.在体育竞赛中取得显著成绩，为国家争得荣誉。非体育专业学生参加省级以上体育比赛获得个人项目前三名，集体项目前二名；高水平运动员（特招生）参加国际和全国性体育比赛获得个人项目前三名、集体项目前二名。集体项目应为主力队员。</span></p><p style="text-align:left;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">&nbsp;&nbsp;&nbsp;&nbsp;6.在重要文艺比赛中取得显著成绩，参加国际和全国性比赛获得前三名，参加省级比赛获得第一名，为国家赢得荣誉。集体项目应为主要演员。</span></p><p style="text-align:left;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">&nbsp;&nbsp;&nbsp;&nbsp;7.获全国三好学生、全国优秀学生干部、全国社会实践先进个人、全国十大杰出青年、中国青年五四奖章等全国性荣誉称号。</span></p><p style="text-align:left;text-indent:32.25pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">除上述七方面之外，如在其他方面有同等级别的特别突出表现，在国家奖学金评审过程中也可作为特别优秀表现提交相关材料。</span></p><p style="text-align:left;text-indent:32.25pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">申请国家奖学金应同时符合以上（一）、（二）、（三）、（四）、（五）、或（六）条。同等条件下，综合测评排名名次居前者优先。转专业的学生在转专业前的学院评选。</span></p><p style="text-align:left;text-indent:-21pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;font-weight:bold;color:#000000;">&nbsp;&nbsp;&nbsp;&nbsp;三、</span><span style="font-size:24px;font-family:&#39;Times New Roman&#39;;background:#ffffff;font-weight:bold;color:#000000;">&nbsp;&nbsp;</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;font-weight:bold;color:#000000;">国家奖学金申请程序</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">（一）学生申请。符合条件的学生，在规定的时间内，根学院具体安排，准备有关材料。</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">（二）学生所在班级评议，并提名。</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">（三）学院初审与公示。学院评审工作小组召开专项评审会议，对本学院申请学生进行评选与审核，将评审结果在学院范围内公示3日。公示无异议，报学生工作部（处）。</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">（四）学生工作部（处）助学管理中心对各学院上报的材料进行复核。</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">(五)学校审核与公示。学校奖学金评审委员会评审，对符合申请条件并通过审核的拟受奖学生名单在全校范围内公示5个工作日。公示无异议，将有关材料报省教育厅，根据批复，按规定程序和标准给予资助。</span></p><p style="text-align:left;text-indent:-21pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;font-weight:bold;color:#000000;">&nbsp;&nbsp;&nbsp;&nbsp;四、</span><span style="font-size:24px;font-family:&#39;Times New Roman&#39;;background:#ffffff;font-weight:bold;color:#000000;">&nbsp;&nbsp;</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;font-weight:bold;color:#000000;">国家奖学金材料报送清单和要求</span></p><p style="text-align:left;text-indent:-21pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">&nbsp;&nbsp;&nbsp;&nbsp;(一)</span><span style="font-size:24px;font-family:&#39;Times New Roman&#39;;background:#ffffff;color:#000000;">&nbsp;&nbsp;&nbsp;</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">材料报送清单</span></p><p style="text-align:left;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;font-weight:bold;color:#000000;">&nbsp;&nbsp;&nbsp;&nbsp;纸质版</span></p><p style="text-align:left;text-indent:-21pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">&nbsp;&nbsp;&nbsp;&nbsp;1.</span><span style="font-size:24px;font-family:&#39;Times New Roman&#39;;background:#ffffff;color:#000000;">&nbsp;&nbsp;</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">《</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">2014-2015</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">学年度本专科生国家奖学金申请审批表》（格式要求参见模板），一式两份。</span></p><p style="text-align:left;text-indent:-21pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">&nbsp;&nbsp;&nbsp;&nbsp;2.</span><span style="font-size:24px;font-family:&#39;Times New Roman&#39;;background:#ffffff;color:#000000;">&nbsp;&nbsp;</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">个人身份证复印件（将身份证正反面复印在同一面</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">A4</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">纸上），一式一份。</span></p><p style="text-align:left;text-indent:-21pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">&nbsp;&nbsp;&nbsp;&nbsp;3.</span><span style="font-size:24px;font-family:&#39;Times New Roman&#39;;background:#ffffff;color:#000000;">&nbsp;&nbsp;</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">“申请条件”中要求的有关材料</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">的原件和</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">复印件，一式一份。</span></p><p style="text-align:left;text-indent:-21pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">&nbsp;&nbsp;4.</span><span style="font-size:24px;font-family:&#39;Times New Roman&#39;;background:#ffffff;color:#000000;">&nbsp;</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">2000</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">字以上的个人先进事迹材料（格式要求参见模板</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">，禁止修改行距、字体等设置</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">）</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">，单面，</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">一式一份。</span></p><p style="text-align:left;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;font-weight:bold;color:#000000;">&nbsp;&nbsp;&nbsp;&nbsp;电子版</span></p><p style="text-align:left;text-indent:-21pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">&nbsp;&nbsp;&nbsp;&nbsp;1.</span><span style="font-size:24px;font-family:&#39;Times New Roman&#39;;background:#ffffff;color:#000000;">&nbsp;&nbsp;</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">《</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">2014-2015</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">学年度本专科生国家奖学金申请审批表》（格式要求参见模板）。</span></p><p style="text-align:left;text-indent:-21pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">&nbsp;&nbsp;&nbsp;&nbsp;2.</span><span style="font-size:24px;font-family:&#39;Times New Roman&#39;;background:#ffffff;color:#000000;">&nbsp;&nbsp;</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">《国家奖学金获奖学生初审名单表》（格式要求参见模板）。</span></p><p style="text-align:left;text-indent:-21pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">&nbsp;&nbsp;&nbsp;&nbsp;3.</span><span style="font-size:24px;font-family:&#39;Times New Roman&#39;;background:#ffffff;color:#000000;">&nbsp;&nbsp;</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">2000</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">字以上的个人先进事迹材料（格式要求参见模板）。</span></p><p style="text-align:left;text-indent:-21pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">&nbsp;&nbsp;&nbsp;&nbsp;4.</span><span style="font-size:24px;font-family:&#39;Times New Roman&#39;;background:#ffffff;color:#000000;">&nbsp;&nbsp;</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#ff0000;">2</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">张个人近期</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">不同的</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">生活照（</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">jpg</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">格式）</span></p><p style="text-align:left;text-indent:-21pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">&nbsp;&nbsp;&nbsp;&nbsp;(二)</span><span style="font-size:24px;font-family:&#39;Times New Roman&#39;;background:#ffffff;color:#000000;">&nbsp;&nbsp;&nbsp;</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">材料报送要求</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">本次国家奖学金申请材料均由申请人本人上交。</span></p><p style="text-align:left;text-indent:-21pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">&nbsp;&nbsp;&nbsp;&nbsp;1.</span><span style="font-size:24px;font-family:&#39;Times New Roman&#39;;background:#ffffff;color:#000000;">&nbsp;&nbsp;</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">上交时间：</span></p><p style="text-align:left;text-indent:-21pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">&nbsp;&nbsp;</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">纸质版</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">2015</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">年</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">10</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">月</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">10</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">日</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">中午</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">12：30</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">-</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">13:1</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">0</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">；</span></p><p style="text-align:left;text-indent:0pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">&nbsp;&nbsp;电子版</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">2015</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">年</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">10</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">月</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">9</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">日晚上</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">22:00</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">点前</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">。</span></p><p style="text-align:left;text-indent:-21pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">&nbsp;&nbsp;&nbsp;&nbsp;2.</span><span style="font-size:24px;font-family:&#39;Times New Roman&#39;;background:#ffffff;color:#000000;">&nbsp;&nbsp;</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">上交地点：</span></p><p style="text-align:left;text-indent:0pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">&nbsp;院楼220门口</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">思源</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">工作室人员处</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">。</span></p><p style="text-align:left;text-indent:0pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">&nbsp;&nbsp;</span><a href="mailto:%E7%94%B5%E5%AD%90%E7%89%88%E4%B8%8A%E4%BA%A4%E5%88%B0%E9%82%AE%E7%AE%B1%EF%BC%9Ayezhimishuseven@163.com"><span style="font-size:24px;text-decoration:underline;font-family:仿宋;background:#ffffff;color:#0563c1;">电子版上交到邮箱：</span></a><span style="font-size:24px;font-family:宋体;background:#ffffff;">1320279417</span><span style="font-size:24px;font-family:宋体;background:#ffffff;">@qq.com</span><span style="font-size:24px;font-family:&#39;Times New Roman&#39;;background:#ffffff;color:#000000;">。</span></p><p style="text-align:left;text-indent:-21pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">&nbsp;&nbsp;&nbsp;&nbsp;3.</span><span style="font-size:24px;font-family:&#39;Times New Roman&#39;;background:#ffffff;color:#000000;">&nbsp;&nbsp;</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">上交要求</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">（1）电子版的所有材料统一命名为“数学与信息学院（或软件学院）xx年级xx专业xx同学+文件名”，然后统一打包并命名为“数学与信息学院（或软件学院）xx年级xx专业xx同学申请国家奖学金材料”。</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">（2）纸质版和电子版的材料填写格式要求、报送材料时间地点安排、材料上交要求均有详细通知，如果申请材料出现逾时上交或者不按要求进行填写材料等问题，后果均由申请人本人承担。</span></p><p style="text-align:left;">&nbsp;</p><p style="text-align:left;text-indent:32pt;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">（3）\r\n根据有关规定，申请国家奖学金的家庭经济困难学生可同时申请并获得国家助学金，但不能同时申请并获得国家励志奖学金；申请国家励志奖学金的学生可以同时申\r\n请并获得国家助学金，但不能同时申请并获得国家奖学金；校内奖学金和国家奖学金双重获得者，按最高等级金额颁发奖金，不重复发放。</span></p><p style="text-align:left;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">如果对于上述通知有任何疑问，请及时沟通联系。</span></p><p style="text-align:left;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">联系人：思源工作室陈嘉兴</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">同学：18814127392</span></p><p style="text-align:left;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">申请者请加入国奖咨询群：&nbsp;476479949（入群后请改备注为“年级+专业+班级+名字”）</span></p><p style="text-align:left;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;数学与信息学院&nbsp;软件学院</span></p><p style="text-align:left;"><span style="font-size:24px;font-family:仿宋;background:#ffffff;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size:24px;font-family:仿宋;background:#ffffff;color:#000000;">二〇一五年十月八日</span></p><p><br/></p>', 11),
(15, '关于做好我院2014年—2015年励志奖学金工作的通知', 1464854463, '思源宣传部', '<p style="text-align:left;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">2012、2013、2014级各班</span><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">：</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">根\r\n据《广东省财政厅广东省教育厅关于印发&lt;广东省普通高校本专科生国家奖助学金管理办法&gt;的通知》（粤财教〔2014〕191号）、《广东省教\r\n育厅关于做好2015年我省高校本专科生国家奖助学金工作的通知》（粤教助函〔2015〕50号）和《华南农业大学本科学生国家奖助学金管理办法》\r\n（〔2014〕151号），以及根据《关于做好2015年我校本科生国家奖助学金工作的通知》，为做好今年我院国家励志奖学金评审工作，现将有关事项通知\r\n如下：</span></p><p style="text-align:left;text-indent:32.15pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;font-weight:bold;color:#000000;">一、国家励志奖学金的申请对象、标准、名额</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">（一）申请对象：</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000;">国家励志奖学金用于奖励全日制二年级及以上（不含评选当年已毕业的学生）品学兼优的家庭经济困难的本科学生。我院</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000;">201</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000;">2</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000;">级、2013级、201</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000;">4</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000;">级学生均可申请。</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">（二）申请标准：</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000;">国家励志奖学金。5000元/人，一次性奖励。</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">（三）名额分配：我院共有180个名额，</span><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">12级、13级、14级各60名额</span><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">。</span></p><p style="text-align:left;text-indent:31.5pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;font-weight:bold;color:#000000;">二、国家励志奖学金申请条件和要求</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">(一)具有中华人民共和国国籍。</span></p><p style="text-align:left;text-indent:23.5pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">（二）热爱社会主义祖国，拥护中国共产党的领导。</span></p><p style="text-align:left;text-indent:23.5pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">（三）遵守宪法和法律，遵守学校规章制度。</span></p><p style="text-align:left;text-indent:24pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">（四）诚实守信，道德品质优良。</span></p><p style="text-align:left;text-indent:24pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">（五）</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000;">学习成绩优秀，2014-2015学年度学习成绩排名为</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;">前50％</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000;">（含）并</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000;">获得评选2014-2015学年度学校优秀三等奖学金以上（含三等奖）</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000;">。</span></p><p style="text-align:left;text-indent:24pt;"><span style="font-size:18px;font-family:Calibri;background:#ffffff;color:#000000;">（六）</span><span style="font-size:18px;font-family:Calibri;background:#ffffff;color:#000000;">家庭经济困难，生活俭朴，</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000;">同时已</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000;">经</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000;">通</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000;">过2014—</span></p><p style="text-align:left;text-indent:24pt;"><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000;">2015学年度家庭经济困难认定</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000;">。</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;">申请国家励志奖学金应同时符合以上（一）、（二）、（三）、（四）、（五）、（六）条。</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000;">转专业的学生在转专业前的学院评选。</span></p><p style="text-align:left;text-indent:31.5pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;font-weight:bold;color:#000000;">三、国家励志奖学金申请程序</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">（一）学生申请。符合条件的学生，在规定的时间内，根据各学院具体安排，准备有关材料。</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">（二）学生所在班级评议，并提名。</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">（三）学院初审与公示。学院评审工作小组召开专项评审会议，对本学院申请学生进行评选与审核，将评审结果在学院范围内公示3日。公示无异议，报学生工作部（处）。</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">（四）学生工作部（处）助学管理中心对各学院上报的材料进行复核。</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">（五）学校审核与公示。学校奖学金评审委员会评审，对符合申请条件并通过审核的拟受奖学生名单在全校范围内公示5个工作日。公示无异议，将有关材料报省教育厅，根据批复，按规定程序和标准给予资助。</span></p><p style="text-align:left;text-indent:31.5pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;font-weight:bold;color:#000000;">四、国家励志奖学金材料报送清单和要求</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">（一）材料报送清单</span></p><p style="text-align:left;text-indent:31.5pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;font-weight:bold;color:#000000;">纸质版（本人交）：</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">1.2014-2015学年度本专科生国家励志奖学金申请审批表》（附件1，参见格式要求附件2），一式两份。</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:16pt;font-family:仿宋;background:#ffffff;color:#000000;"><span style="font-size:18px;">2.</span></span><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">身份证复印件（将身份证正反面复印在同一面A4纸上），一式一份。</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;"><span style="font-size:18px;">3.</span></span><span style="font-size:18px;font-family:仿宋_GB2312;">“申请条件”中要求的有关材料的原件和复印件</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">4.2000字以上的个人先进事迹材料（参见格式要求附件3）。</span><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">（格式要求参见模板，禁止修改行距、字体等设置），单面，一式一份。</span></p><p style="text-align:left;text-indent:32pt;">&nbsp;</p><p style="text-align:left;text-indent:32.15pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;font-weight:bold;color:#000000;">电子版（以班级为单位，即生委发）：：</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">1.《2014-2015学年度本专科生国家励志奖学金申请审批表》。</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">2.《国家励志奖学金获奖学生初审名单表》（附件4，参见格式要求附件5，以班级为单位填写并发送）</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">3.2000字以上的个人先进事迹材料（参见格式要求附件3）。</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">4.2张个人近期生活照（jpg格式）</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">(二)材料报送要求</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">本次国家励志奖学金申请材料均由申请人</span><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">本人</span><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">上交。</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">1.上交时间：</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">纸质版2015年10月12日中午12点30分-13点15分</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">电子版2015年10月11日22：00。</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">2.上交地点：</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">院楼220门口思源工作室人员处。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">3.上交邮箱：</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">2014级：jjscau_sxrsy_02@163.com</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">2013级：jjscau_sxrsy_03@163.com</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">2012级：jjscau_sxrsy_04@163.com</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">（<span style="font-size:18px;font-family:仿宋;">1</span><span style="font-size:18px;font-family:宋体;">）电子版的所有材料统一命名为“数学与信息学院（或软件学院）</span><span style="font-size:18px;font-family:仿宋;">xx</span><span style="font-size:18px;font-family:宋体;">年级</span><span style="font-size:18px;font-family:仿宋;">xx</span><span style="font-size:18px;font-family:宋体;">专业</span><span style="font-size:18px;font-family:仿宋;">xx</span><span style="font-size:18px;font-family:宋体;">同学</span><span style="font-size:18px;font-family:仿宋;">+</span><span style="font-size:18px;font-family:宋体;">文件名”，然后统一打包并命名为“数学与信息学院（或软件学院）</span><span style="font-size:18px;font-family:仿宋;">xx</span><span style="font-size:18px;font-family:宋体;">年级</span><span style="font-size:18px;font-family:仿宋;">xx</span><span style="font-size:18px;font-family:宋体;">专业</span><span style="font-size:18px;font-family:仿宋;">xx</span><span style="font-size:18px;font-family:宋体;">同学申请国家奖学金材料”。</span></span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">（<span style="font-size:18px;font-family:仿宋;">2</span><span style="font-size:18px;font-family:宋体;">）纸质版和电子版的材料填写格式要求、报送材料时间地点安排、材料上交要求均有详细通知，如果申请材料出现逾时上交或者不按要求进行填写材料等问题，后果均由申请人本人承担。</span></span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">（3）根据有关规定，</span><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">申请国家奖学金的家庭经济困难学生可同时申请并获得国家助学金</span><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">，但不能同时获得国家励志奖学金；申请国家励志奖学金的学生可以同时申请并获得国家助学金，但不能同时获得国家奖学金；</span><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">校内奖学金和国家奖学金双重获得者，按最高等级金额颁发奖金，不重复发放。</span></p><p style="text-align:left;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">如果对于上述内容有任何疑问，请及时沟通联系。联系人：胡博婷同学：18814126161</span></p><p style="text-align:left;">&nbsp;</p><p style="text-align:left;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 数学与信息学院&nbsp;软件学院</span></p><p style="text-align:left;"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000;">&nbsp;二〇一五年十月八日</span></span></p><p><br/></p>', 10);
INSERT INTO `sy_article` (`id`, `subject`, `lastmodifytime`, `author`, `message`, `createtime`) VALUES
(16, '关于做好我院2015—2016年度国家助学金评选工作的通知 ', 1464854500, '思源宣传部', '<p style=";text-align:left;line-height:26px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">各年级各班</span><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">：</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">根 据《广东省财政厅广东省教育厅关于印发&lt;广东省普通高校本专科生国家奖助学金管理办法&gt;的通知》（粤财教〔2014〕191号）、《广东省教 育厅关于做好2015年我省高校本专科生国家奖助学金工作的通知》（粤教助函〔2015〕50号）和《华南农业大学本科学生国家奖助学金管理办法》 （〔2014〕151号），以及根据《关于做好2015年我校本科生国家奖助学金工作的通知》，为做好今年我院国家助学金评审工作，现将有关事项通知如 下：</span></p><p style=";text-align:left;line-height:26px;text-indent:42px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;font-weight:bold;color:#000000">一、国家助学金的申请对象、标准、名额</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">（一）申请对象：</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000">国家助学金用于资助全日制在读家庭经济困难的本科学生。我院</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000">201</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000">2</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000">级、201</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000">3</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000">级、201</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000">4</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000">级、201</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000">5</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000">级符合条件的同学均可申请。</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">（二）申请标准：</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000">3000元/人；根据要求，分2015-2016学年度秋季和春季两学期发放，</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000">每学期发放1500元</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000">。</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000">（三）</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000">按 照各年级2015—2016年度家庭经济困难学生人数的比例进行划分，各年级的名额原则上分别是：2012级149人，&nbsp;2013级180人，&nbsp;2014 级183人，2015级214人。各年级如发生名额剩余的情况（如有学生不按规定时间或模板要求提交申请材料，以及因本人原因放弃申请等），可在学院评审 小组讨论后进行调整。</span></p><p style=";text-align:left;line-height:26px;text-indent:42px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;font-weight:bold;color:#000000">二、国家助学金申请条件和要求</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">（一）具有中华人民共和国国籍。</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">（二）热爱社会主义祖国，拥护中国共产党的领导。</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">（三）遵守宪法和法律，遵守学校规章制度。</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">（四）诚实守信，道德品质优良。</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">（五）</span><span style="font-size:18px;font-family:Calibri;background:#ffffff;color:#000000">家庭经济困难，生活俭朴，并</span><span style="font-size:18px;font-family:Calibri;background:#ffffff;color:#000000">2015-2016<span style="font-family:仿宋_GB2312"><span style="font-size:18px">学年年度已在学校家庭经济困难学生资料库备案</span></span></span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000">，即通过</span><span style="font-size:18px;font-family:Calibri;background:#ffffff;color:#ff0000">2015-2016<span style="font-family:仿宋_GB2312"><span style="font-size:18px">学年</span></span></span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#ff0000">年度</span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000">的贫困生认定程度</span><span style="font-size:18px;font-family:Calibri;background:#ffffff;color:#000000">。</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:Calibri;background:#ffffff;color:#000000">申请国家助学金应同时符合以上的（一）、（二）、（三）、（四）、（五）条。同时，申请学生经过了<span style="font-family:Calibri"><span style="font-size:18px">2015—2016</span></span><span style="font-family:仿宋_GB2312"><span style="font-size:18px">学年度家庭经济困难认定。没有在奖助学系统进行填报、贫困生认定材料不合格同学</span></span></span><span style="font-size:18px;font-family:仿宋_GB2312;background:#ffffff;color:#000000">、或</span><span style="font-size:18px;font-family:Calibri;background:#ffffff;color:#000000">没有按时补交材料，不得申请。转专业的学生在</span><span style="font-size:18px;font-family:Calibri;background:#ffffff;color:#ff0000">转专业后</span><span style="font-size:18px;font-family:Calibri;background:#ffffff;color:#000000">的学院评选。</span></p><p style=";text-align:left;line-height:26px;text-indent:42px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;font-weight:bold;color:#000000">二、国家助学金申请程序</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">（一）学生申请。符合条件的学生，在规定的时间内，根据各学院具体安排，准备有关材料。</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">（二）学生所在班级评议，并提名。</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">（三）学院初审与公示。学院评审工作小组召开专项评审会议，对本学院申请学生进行评选与审核，将评审结果在学院范围内公示3日。公示无异议，报学生工作部（处）。</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">（四）学生工作部（处）助学管理中心对各学院上报的材料进行复核。</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">（五）学校审核与公示。学校奖学金评审委员会评审，对符合申请条件并通过审核的拟受奖学生名单在全校范围内公示5个工作日。公示无异议，将有关材料报省教育厅，根据批复，按规定程序和标准给予资助。</span></p><p style=";text-align:left;line-height:26px;text-indent:42px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;font-weight:bold;color:#000000">三、国家助学金材料报送清单和要求</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">（一）材料报送清单</span></p><p style=";text-align:left;line-height:26px;text-indent:42px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;font-weight:bold;color:#000000">纸质版</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">1.《2014-2015学年度国家助学金申请审批表》（参见模板），一式一份。</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">2.个人身份证复印件（将身份证正反面复印在同一面A4纸上），一式一份。</span></p><p style=";text-align:left;line-height:26px;text-indent:42px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;font-weight:bold;color:#000000">电子版</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">1.《2014-2015学年度国家助学金申请审批表》。</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">2.《国家助学金受助学生初审名单表》（参见模板）。</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">3.2000字以上的个人材料（参见模板）。</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">4.2张个人近期生活照（jpg格式）。</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">（二）材料报送要求</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">本次国家助学金申请材料（包括纸质版和电子版材料）均以班级为单位上交。材料报送负责人：各年级均由2015-2016年度在任的生活委员负责。</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">1.上交时间：</span></p><p style=";text-align:left;margin:0 0 0 43px;line-height:26px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">纸质版2015年10月16日中午12点30分-13点30分；电子版2015年10月15日晚上22点前。</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">2.上交地点：</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">院楼220思源工作室人员处；</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">3.</span><a href="mailto:%E7%94%B5%E5%AD%90%E7%89%88%E4%B8%8A%E4%BA%A4%E5%88%B0%E9%82%AE%E7%AE%B1%EF%BC%9Ayezhimishuseven@163.com"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">电子版上交到邮箱：</span></a></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">2015级：scau_sxrsy_01@163.com</span><span style="font-size:21px;font-family:仿宋;background:#ffffff;color:#000000"><br/> </span><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">&nbsp;&nbsp;&nbsp;&nbsp;2014级：scau_sxrsy_02@163.com</span><span style="font-size:21px;font-family:仿宋;background:#ffffff;color:#000000"><br/> </span><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">&nbsp;&nbsp;&nbsp;&nbsp;2013级：scau_sxrsy_03@163.com</span><span style="font-size:21px;font-family:仿宋;background:#ffffff;color:#000000"><br/> </span><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">&nbsp;&nbsp;&nbsp;&nbsp;2012级：scau_sxrsy_04@163.com&nbsp;</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:14px;font-family:&#39;Times New Roman&#39;"><span style="font-size:18px">&nbsp;</span></span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">（1）电子版:所有同学的个人材料统一命名为“数学与信息学院（或软件学院）xx年级xx专业xx班级xx同学+文件名”，统一打包后（命名为““数学与信息学院（或软件学院）xx年级xx专业xx同学申请国家助学金材料”）上交到各班生活委员。</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">生活委员将班级同学材料收集后，统一打包并命名为“数学与信息学院（或软件学院）xx年级xx专业xx班级申请国家助学金材料”发送到上述邮箱。注意，只接受生活委员统一提交的纸质版材料和电子版材料，以个人名义提交的，不予受理。</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">（2） 根据有关规定，申请国家奖学金的家庭经济困难学生可同时申请并获得国家助学金，但不能同时获得国家励志奖学金；申请国家励志奖学金的学生可以同时申请并获 得国家助学金，但不能同时获得国家奖学金；校内奖学金和国家奖学金双重获得者，按最高等级金额颁发奖金，不重复发放。</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">（3）纸质版和电子版的材料填写格式要求、报送材料时间地点安排、材料上交要求均有详细通知，如果申请材料出现逾时上交或者不按要求进行填写材料等问题，后果均由材料报送负责人和申请人本人共同承担。</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">如果对于上述内容有任何疑问，请及时沟通联系。联系人：邹志莲同学：18814126529</span></p><p style=";text-align:left;line-height:26px;text-indent:43px"><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">数学与信息学院&nbsp;软件学院</span></p><p><span style="font-size:18px;font-family:仿宋;background:#ffffff;color:#000000">&nbsp;&nbsp;&nbsp;二〇一五年十月九日</span></p><p><br/></p>', 13),
(17, '关于做好我院本科生“模范引领计划”工作的通知', 1464854634, '思源宣传部', '<p style="text-indent:32pt;"><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">为进一步推动我</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">院</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">学风校风建设，在全</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">院</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">大学生中营造创先争优的良好氛围，充分发挥榜样的示范引领作用，激励大学生勤奋学习、努力进取、开拓创新，德智体美等全面发展，学</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">院</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">决定在本科生综合测评及评优工作的基础上，开展第二届本科“模范引领计划”评选工作。现将有关事宜通知如下：</span></p><p style="text-indent:32pt;"><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">一、“模范引领计划”奖项设置</span></p><p style="text-indent:32pt;"><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">“模范引领计划”设10类奖项，每类奖项设10名标兵奖、10名提名奖，分别奖励不同领域有卓越表现的学生或集体，具体如下：&nbsp;</span></p><p style="text-indent:32pt;"><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">华农之星标兵奖、学术之星标兵奖、学习之星标兵奖、创业之星标兵奖、道德之星标兵奖、自强之星标兵奖、学干之星标兵奖、文体之星标兵奖、星级先进班集体标兵奖、星级文明宿舍标兵奖。</span></p><p style="text-indent:32pt;"><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">二、学院成立院级“模范引领计划”工作领导小组</span></p><p style="text-indent:32pt;"><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">组长：郭灼（党委副书记）</span></p><p style="text-indent:32pt;"><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">组员：詹瑾妮、梁辉、毛丹鹃、戴文浪、陈思、刘树鑫、余丹华、倪慧娜、黄经纬、班主任代表、学生代表。</span></p><p><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">三</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">、评选对象</span></p><p style="text-indent:32pt;"><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">我校全日制在读二年级以上（含二年级）本科生或集体。</span></p><p style="text-indent:32pt;"><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">三、评选条件</span></p><p style="text-indent:24pt;"><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">（一）“模范引领计划”评选的</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;background:#ffff00;">基本条件</span></p><p style="text-indent:32pt;"><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">1.热爱社会主义祖国，拥护党的路线、方针、政策，严格遵守法律法规、具有良好的道德品质，自觉践行社会主义核心价值观。</span></p><p style="text-indent:32pt;"><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">2.积极进取，在个人或集体的成长发展中成绩突出，堪为学生中的典范，对青年学生健康成长起到重要的示范和导向作用。</span></p><p style="text-indent:32pt;"><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">3.模范引领计划各奖项的申请者须为该领域最优秀的个人或集体，且符合各项目的具体申请条件。</span></p><p style="text-indent:32pt;"><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">4.2014年获得“模范引领计划”8类奖项</span><span style="font-size:16pt;text-decoration:underline;font-family:&#39;仿宋_GB2312&#39;;">标兵奖</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">的个人不参加</span><span style="font-size:16pt;text-decoration:underline;font-family:&#39;仿宋_GB2312&#39;;">本次评选</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">；获得星级先进班集体和星级文明宿舍标兵奖，以及获得10类奖项提名奖的个人和集体可以参加今年的评选。</span></p><p><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp;5.本次评选</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">除华农之星外，其他奖项均不能重复申请（即只有“华农之星”可以与其他奖项同时申请，但不能同时获奖）。</span></p><p style="text-indent:32pt;"><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">6.学生提交的材料通过所在学院审核，并获得学院推荐资格。</span></p><p style="text-indent:24pt;"><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">（二）“模范引领计划”各奖项评选的</span><span style="font-size:16pt;text-decoration:underline;font-family:&#39;仿宋_GB2312&#39;;">具体条件</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">：</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;color:#ff0000;">详见附件《数学与信息学院</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;color:#ff0000;">&nbsp;软件学院本科生“模范引领计划”评选实施细则</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;color:#ff0000;">》</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;color:#ff0000;">。</span></p><p style="text-indent:32pt;"><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">四、评选程序</span></p><p style="text-indent:32pt;"><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">1.\r\n学生个人或集体申请。符合条件的学生个人或集体，在10月26日至11月3日，根据各学院具体安排，其中申请华农之星、学术之星、学习之星、创业之星、道\r\n德之星、自强之星、学干之星和文体之星等8类奖项的学生需要准备纸质版《华南农业大学第二届本科生“模范引领计划”学生申请表》（附件1，以下简称《学生\r\n申请表》，限A4纸正反面打印）、相关证明材料复印件和个人先进事迹材料（材料要求和样式详见附件2）；电子版个人先进事迹材料和个人近期生活照2张</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">（照片为JPG格式；</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">以上材料统一打包，命名为“xx学院</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">+专业班级</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">+学生姓名</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">+申请xx之星的材料</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">”）。</span></p><p><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp;申请星级先进班集体的集\r\n体需要准备纸质版《华南农业大学第二届本科生“模范引领计划”星级班集体申请表》（附件3，以下简称《星级班集体申请表》，限A4纸正反面打印）、相关证\r\n明材料复印件和集体先进事迹材料（材料要求和样式详见附件4）；电子版集体先进事迹材料和集体近期生活照5张</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">（照片为JPG格式，其中1张为班集体全体成员合影，其余4张为班级学习生活等精彩瞬间；</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">以上材料统一打包，命名为“xx学院+xx专业+xx级+xx班</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">+xx先进班集体材料</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">”）。</span></p><p><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp;申请星级文明宿舍的集体\r\n需要准备纸质版《华南农业大学第二届本科生“模范引领计划”星级文明宿舍申请表》（附件5，以下简称《星级文明宿舍申请表》，限A4纸正反面打印）、相关\r\n证明材料复印件和集体先进事迹材料（材料要求和样式详见附件6）；电子版宿舍先进事迹材料和宿舍成员近期生活照5张</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">;</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">（照片为JPG格式，其中1张为宿舍全体成员合影，其余4张为宿舍学习生活等精彩瞬间；</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">以上材料统一打包，命名为“xx学院+xx专业+xx级+xx班+xx宿舍”）。</span></p><p style="text-indent:32pt;"><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">请申请学生个人或集体准备以上材料向所在学院提出申请。以上所有纸质版材料（均一式一份）和电子版材料报送至学院指定地点和邮箱。</span></p><p style="text-indent:32pt;"><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">2.学院召开评审会议。对最终获得院级的&quot;模范引领&quot;十大之星的学生名单进行公示三天，并推荐和指导特别优秀的学生参加学校的&quot;模范引领&quot;十大之星的评选。</span></p><p><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;font-weight:bold;">&nbsp;&nbsp;&nbsp;五、报送材料</span></p><p style="text-indent:32pt;"><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">（一）报送材料时间</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">1.请申请者于2015年</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;background:#ffff00;">11月2日（星期一）</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">中午</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;background:#ffff00;">12:30-13:00</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">纸质版材料报送至学院院楼220门口思源工作室工作人员处；</span></p><p style="text-align:left;text-indent:32pt;"><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">2.请申请者于2015年</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;background:#ffff00;">11月1日（星期日）</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">晚上</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">9</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">:00之前将电子版材料发送至指定邮箱</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;background:#ffff00;">scau_sxrsy_all@163.com</span><a href="mailto:%E5%8D%8E%E5%8D%97%E5%86%9C%E4%B8%9A%E5%A4%A7%E5%AD%A62014%E5%B9%B4%E5%B9%BF%E4%B8%9C%E7%9C%81%E5%AE%B6%E5%BA%AD%E7%BB%8F%E6%B5%8E%E5%9B%B0%E9%9A%BE%E6%9C%AC%E7%A7%91%E6%96%B0%E7%94%9F%E8%B5%84%E5%8A%A9%E6%83%85%E5%86%B5%E8%A1%A8%E3%80%8B%EF%BC%88%E9%99%84%E4%BB%B63%EF%BC%89%E7%94%B5%E5%AD%90%E7%89%88%E5%8F%91%E9%80%81%E8%87%B3zxdk@scau.edu.cn"><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">。</span></a></p><p style="text-indent:32pt;"><img style="height:111px;width:120px;" alt="" src="http://7xlp0l.com1.z0.glb.clouddn.com/14648546329.png" border="0" height="428" width="428"/></p><p><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">数学与信息学院</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;软件学院</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;</span></p><p><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">2015年10月</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">26</span><span style="font-size:16pt;font-family:&#39;仿宋_GB2312&#39;;">日</span></p><p><br/></p>', 10),
(18, '关于做好我院2015-2016学年中国肯德基曙光基金资助工作的通知', 1464854683, '思源宣传部', '<p><span style="font-size:14pt;font-family:仿宋_GB2312;">2015</span><span style="font-size:14pt;font-family:仿宋_GB2312;">级各班：</span></p><p style="text-indent:28pt;"><span style="font-size:14pt;font-family:仿宋_GB2312;">“中国肯德基曙光基金”是由百胜（中国）投资有限公司及其所属<a href="http://www.baike.com/sowiki/%E8%82%AF%E5%BE%B7%E5%9F%BA%E5%85%AC%E5%8F%B8?prd=content_doc_search"><span style="text-decoration:none;color:windowtext;">肯德基公司</span></a>（简称百胜餐饮集团）委托中国青少年发展基金会设立的资助贫困大学生的专项公益基金。曙光基金实施13年来，已经形成了一套融入肯德基独特资源的三位一体的资助模式：学业资助+餐厅实践+社会服务。由中国肯德基曙光基金会发起的资助贫困大学生项目今年在我校开展资助。根据资助方的要求，结合学校实际，为做好我校申请工作，现将有关事宜通知如下：</span></p><p style="text-indent:28.1pt;"><strong><span style="font-size:14pt;font-family:仿宋_GB2312;">一、资助对象</span></strong></p><p style="text-indent:28pt;"><span style="font-size:14pt;font-family:仿宋_GB2312;">我院2015年家庭经济特别困难本科新生。</span></p><p><span style="font-size:14pt;font-family:仿宋_GB2312;">&nbsp;&nbsp;&nbsp; </span><strong><span style="font-size:14pt;font-family:仿宋_GB2312;">二、资助标准与人数</span></strong></p><p><span style="font-size:14pt;font-family:仿宋_GB2312;">&nbsp;&nbsp;&nbsp; </span><span style="font-size:14pt;font-family:仿宋_GB2312;">一至四年级按7000元、5000元、5000元、3000元的额度发放，如无特殊情况，经考评合格，受助学生可以连续申请资助，直到本科毕业。资助款按学年划拨，由曙光基金理事会办公室直接划入受助生个人银行账户。</span></p><p style="text-indent:28pt;"><span style="font-size:14pt;font-family:仿宋_GB2312;">注：初选人数为30人，通过到肯德基餐厅参观、座谈等环节，相互了解，双向选择，最后与省级青基会和肯德基公司共同选拔受助新生15人。我院有2个名额。（此为初选名额，参加11月22日与资助方见面会，通过到肯德基餐厅参观、座谈等环节，相互了解，双向选择，最终的资助对象由省级青基会和肯德基公司共同选拔。）</span></p><p style="text-indent:28.1pt;"><strong><span style="font-size:14pt;font-family:仿宋_GB2312;">三、申请时间&nbsp;&nbsp; </span></strong></p><p><span style="font-size:14pt;font-family:仿宋_GB2312;">&nbsp;&nbsp;&nbsp; </span><span style="font-size:14pt;font-family:仿宋_GB2312;">学生申请时间为2015年11月6日至8日。</span></p><p><span style="font-size:14pt;font-family:仿宋_GB2312;">&nbsp;&nbsp; <strong>&nbsp;</strong></span><strong><span style="font-size:14pt;font-family:仿宋_GB2312;">四、申请条件</span></strong></p><p><span style="font-size:14pt;font-family:仿宋_GB2312;">&nbsp;&nbsp;&nbsp; 1. </span><span style="font-size:14pt;font-family:仿宋_GB2312;">家庭贫困，无力支付大学期间全部或部分学习和生活费用；</span></p><p style="text-indent:31.5pt;"><span style="font-size:14pt;font-family:仿宋_GB2312;">2. </span><span style="font-size:14pt;font-family:仿宋_GB2312;">热爱祖国，思想进步，明礼诚信，团结友善，生活简朴；</span></p><p style="text-indent:31.5pt;"><span style="font-size:14pt;font-family:仿宋_GB2312;">3. </span><span style="font-size:14pt;font-family:仿宋_GB2312;">遵守国家法律，遵守《高等学校学生行为准则》和我校各项规章制度，道德品质良好，无违纪行为。</span></p><p style="text-indent:28pt;"><span style="font-size:14pt;font-family:仿宋_GB2312;">4. </span><span style="font-size:14pt;font-family:仿宋_GB2312;">在学习的同时注重自身素质的提高，积极参与学校的各项活动，并有优秀的表现。</span></p><p style="text-indent:28pt;"><span style="font-size:14pt;font-family:仿宋_GB2312;">5. </span><span style="font-size:14pt;font-family:仿宋_GB2312;">充分认同曙光基金理念，愿意积极参加餐厅实践和社会服务项目，学以致用，提升能力，回报社会。</span></p><p style="text-indent:28pt;"><span style="font-size:14pt;font-family:仿宋_GB2312;">6. </span><span style="font-size:14pt;font-family:仿宋_GB2312;">没有申请其他由中国青少年发展基金会或广东省青少年发展基金会设计的资助项目。</span></p><p style="text-indent:28pt;"><span style="font-size:14pt;font-family:仿宋_GB2312;">7.</span><span style="font-size:14pt;font-family:仿宋_GB2312;">良好的感恩意识。</span></p><p><strong><span style="font-size:14pt;font-family:仿宋_GB2312;">&nbsp; </span></strong><span style="font-size:14pt;font-family:仿宋_GB2312;">&nbsp;&nbsp;</span><strong><span style="font-size:14pt;font-family:仿宋_GB2312;">五、申请程序</span></strong></p><p style="text-indent:28pt;"><span style="font-size:14pt;font-family:仿宋_GB2312;">1</span><span style="font-size:14pt;font-family:仿宋_GB2312;">．学生申请。符合条件的学生，在学校规定的时间内，根据各学院具体安排，准备纸质版申请书（一式一份，<span style="color:red;">800</span><span style="color:red;">字以上</span>，统一使用有学校抬头的<span style="color:red;">信签纸</span>手写，内容包含父母职业、家庭人口、收入来源情况、学费减免及获得奖学金、助学金状况），《中国肯德基曙光基金申请表》（附件2，一式三份，以下简称申请表，<span style="color:red;">手动填写，不能打印）</span>，<span style="color:red;">《华南农业大学2015年中国肯德基曙光基金申报学生信息汇总表》（附件3，一式一份，以下简称汇总表），学生家庭所在街道或镇以上民政部门开具的家庭经济状况证明复印件</span>；电子版汇总表（发送到</span><span style="font-size:13.5pt;font-family:仿宋;background:white;color:black;">2015</span><span style="font-size:13.5pt;font-family:仿宋;background:white;color:black;">级公邮：scau_sxrsy_01@163.com</span><span style="font-size:14pt;font-family:仿宋_GB2312;">），向所在学院提出申请。</span></p><p><span style="font-size:14pt;font-family:仿宋_GB2312;">&nbsp;&nbsp;&nbsp; 2</span><span style="font-size:14pt;font-family:仿宋_GB2312;">．学院初审与公示。评审结果在学院范围内公示3日。公示无异议，报学生工作部（处）。</span></p><p><span style="font-size:14pt;font-family:仿宋_GB2312;">&nbsp;&nbsp;&nbsp; 3</span><span style="font-size:14pt;font-family:仿宋_GB2312;">．学校审核与公示。学校对各学院上报名单进行审核，对符合申请条件并通过审核的拟资助学生名单在全校范围内公示3个工作日。公示无异议，将有关材料报中国青少年发展基金会，资助方按规定程序和标准给予资助。 </span></p><p><span style="font-size:14pt;font-family:仿宋_GB2312;">&nbsp;&nbsp;&nbsp; </span><strong><span style="font-size:14pt;font-family:仿宋_GB2312;">六、报送材料</span></strong></p><p style="text-align:left;text-indent:28pt;"><span style="font-size:14pt;font-family:仿宋_GB2312;">请2015级以班级为单位，将纸质材料于2015年11月9日（星期一）中午12:30至13：00间将纸质版学生申请书（一式一份）、申请表（一式三份）、汇总表（一式一份）、学生家庭所在街道或镇以上民政部门开具的家庭经济状况证明复印件，报送至泰山九栋楼下思源工作室的同学。电子版汇总表（班级生委将学生信息汇总）于11月8号晚上8点前发送至指定邮箱</span><span style="font-size:13.5pt;font-family:仿宋;background:white;color:black;"><a href="mailto:scau_sxrsy_01@163.com">scau_sxrsy_01@163.com</a></span><span style="font-size:14pt;font-family:仿宋_GB2312;">。</span></p><p style="text-indent:28.1pt;"><strong><span style="font-size:14pt;font-family:仿宋_GB2312;">七、工作要求&nbsp;&nbsp; </span></strong></p><p><span style="font-size:14pt;font-family:仿宋_GB2312;">&nbsp;&nbsp;&nbsp; 1. </span><span style="font-size:14pt;font-family:仿宋_GB2312;">学院将坚持公平、公开、公正的原则，在规定时间内完成评选工作。</span></p><p><span style="font-size:14pt;font-family:仿宋_GB2312;">&nbsp;&nbsp;&nbsp; 2. </span><span style="font-size:14pt;font-family:仿宋_GB2312;">申请学生自愿接受资助的同时，根据资助方要求，受助学生大一至大二上半学期期间到肯德基餐厅参加实践，了解社会，培养自己的基本社会生活技能。餐厅实践期间遵守餐厅各项规章制度，总工时不低于400小时，同时享受普通计时工各项待遇；参加曙光公社社团，并在大二、大三期间全程参与社会服务项目及公益活动，不低于200小\r\n时；毕业工作后视个人情况自愿向基金捐资，帮助其他学生从中受益。毕业后主动向基金办公室提供持续稳定的联络方式，如有变动，及时通知基金办公室本人新的\r\n联络方式。受助生每年要从思想品德、学习生活和在肯德基餐厅实践及曙光公社社会服务四个方面进行考评。受助学生学年内无不及格科目，各科考试总成绩排列名\r\n次在全班前50%。在校的考评鉴定由校团委（学生工作部）做出，餐厅实践考评鉴定由肯德基餐厅和肯德基公司做出，社会服务项目\r\n考评鉴定由学校和肯德基公司做出，考评合格者继续资助。如受助生没有继续申请资助的意愿，或受助生在学习、餐厅实践、社会服务方面未达到基金资助要求，终\r\n止对其资助。</span></p><p><span style="font-size:14pt;font-family:仿宋_GB2312;">&nbsp;&nbsp;&nbsp; </span><span style="font-size:14pt;font-family:仿宋_GB2312;">联系人：刘老师 倪老师 戴老师；联系电话：38295704</span></p><p>&nbsp;</p><p>&nbsp;</p><p><span style="font-size:14pt;font-family:仿宋_GB2312;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size:14pt;font-family:仿宋_GB2312;">数学与信息学院 软件学院</span></p><p><span style="font-size:14pt;font-family:仿宋_GB2312;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style="font-size:14pt;font-family:仿宋_GB2312;">二零一五年十一月六日</span></p><p><br/></p>', 10);
INSERT INTO `sy_article` (`id`, `subject`, `lastmodifytime`, `author`, `message`, `createtime`) VALUES
(19, '关于做好我院2015-2016学年度仲明助学金评选工作的通知', 1464854716, '思源宣传部', '<p><span style="font-family:Calibri;"><span style="font-size:18px;font-family:仿宋_GB2312;">2015</span><span style="font-size:18px;font-family:仿宋_GB2312;">级各班：</span></span></p><p><span style="font-family:Calibri;"><span style="font-size:16pt;font-family:仿宋_GB2312;"><span style="font-size:18px;">&nbsp;&nbsp;&nbsp; </span></span><span style="font-size:18px;font-family:仿宋_GB2312;">碧桂园控股有限公司董事局主席杨国强先生于<span style="font-size:18px;">1997</span>年设立仲明助学金，每年捐资<span style="font-size:18px;">100</span>万元，到<span style="font-size:18px;">2006</span>年增至每年捐资<span style="font-size:18px;">200</span>万元，向广东部分高校家庭经济困难的优秀学生提供资助，<span style="font-size:18px;">2006</span>年至今累计资助八千多名家庭经济困难的优秀学生。根据仲明助学金管委会《关于评选<span style="font-size:18px;">2015</span>学年仲明助学金的通知》，结合学校实际，现将评选有关事宜通知如下：</span></span></p><p><span style="font-family:Calibri;"><span style="font-size:16pt;font-family:仿宋_GB2312;"><span style="font-size:18px;">&nbsp;&nbsp;&nbsp; </span></span><strong><span style="font-size:18px;font-family:仿宋_GB2312;">一、资助对象</span></strong></span></p><p><span style="font-family:Calibri;"><span style="font-size:16pt;font-family:仿宋_GB2312;"><span style="font-size:18px;">&nbsp;&nbsp;&nbsp; </span></span><span style="font-size:18px;font-family:仿宋_GB2312;">我校<span style="font-size:18px;">2015</span>级全日制在读本科生。</span></span></p><p><span style="font-family:Calibri;"><span style="font-size:16pt;font-family:仿宋_GB2312;"><span style="font-size:18px;">&nbsp;&nbsp;&nbsp; </span></span><strong><span style="font-size:18px;font-family:仿宋_GB2312;">二、资助标准与名额分配</span></strong></span></p><p><span style="font-family:Calibri;"><span style="font-size:18px;font-family:仿宋_GB2312;"><span style="font-size:18px;">&nbsp;&nbsp;&nbsp; </span>1.</span><span style="font-size:18px;font-family:仿宋_GB2312;">资助标准：<span style="font-size:18px;">3000</span>元<span style="font-size:18px;">/</span>人，一次性资助。</span></span></p><p><span style="font-family:Calibri;"><span style="font-size:18px;font-family:仿宋_GB2312;"><span style="font-size:18px;">&nbsp;&nbsp;&nbsp; </span>2.</span><span style="font-size:18px;font-family:仿宋_GB2312;">名额分配：我院有<span style="font-size:18px;">2</span>个名额。</span></span></p><p><span style="font-family:Calibri;"><span style="font-size:16pt;font-family:仿宋_GB2312;"><span style="font-size:18px;">&nbsp;&nbsp; </span><strong><span style="font-size:18px;">&nbsp;</span></strong></span><strong><span style="font-size:18px;font-family:仿宋_GB2312;">三、申请时间<span style="font-size:18px;">&nbsp; </span></span></strong><span style="font-size:16pt;font-family:仿宋_GB2312;"><span style="font-size:18px;">&nbsp;</span></span></span></p><p><span style="font-family:Calibri;"><span style="font-size:16pt;font-family:仿宋_GB2312;"><span style="font-size:18px;">&nbsp;&nbsp;&nbsp; </span></span><span style="font-size:18px;font-family:仿宋_GB2312;">学生申请时间为<span style="font-size:18px;">2015</span>年<span style="font-size:18px;">11</span>月<span style="font-size:18px;">6</span>日至<span style="font-size:18px;">11</span>月<span style="font-size:18px;">8</span>日。</span></span></p><p><span style="font-family:Calibri;"><span style="font-size:16pt;font-family:仿宋_GB2312;"><span style="font-size:18px;">&nbsp;&nbsp;&nbsp; </span></span><strong><span style="font-size:18px;font-family:仿宋_GB2312;">四、评选条件</span></strong></span></p><p><span style="font-family:Calibri;"><span style="font-size:18px;font-family:仿宋_GB2312;"><span style="font-size:18px;">&nbsp;&nbsp;&nbsp; </span>1.</span><span style="font-size:18px;font-family:仿宋_GB2312;">热爱祖国，遵纪守法，举止文明，热心公益活动。</span></span></p><p><span style="font-family:Calibri;"><span style="font-size:18px;font-family:仿宋_GB2312;"><span style="font-size:18px;">&nbsp;&nbsp;&nbsp; </span>2.</span><span style="font-size:18px;font-family:仿宋_GB2312;">诚实守信，道德品质优良，认同捐赠方的慈善理念。</span></span></p><p><span style="font-family:Calibri;"><span style="font-size:18px;font-family:仿宋_GB2312;"><span style="font-size:18px;">&nbsp;&nbsp;&nbsp; </span>3.</span><span style="font-size:18px;font-family:仿宋_GB2312;">家庭经济特别困难，生活简朴，无不良嗜好。</span></span></p><p><span style="font-family:Calibri;"><span style="font-size:18px;font-family:仿宋_GB2312;"><span style="font-size:18px;">&nbsp;&nbsp;&nbsp; </span>4.</span><span style="font-size:18px;font-family:仿宋_GB2312;">勤奋学习，积极上进，学习优秀。</span></span></p><p style="text-indent:32pt;"><span style="font-family:Calibri;"><span style="font-size:18px;font-family:仿宋_GB2312;">5.</span><span style="font-size:18px;font-family:仿宋_GB2312;">受灾地区学生优先考虑。</span></span></p><p style="text-indent:32pt;"><span style="font-family:Calibri;"><span style="font-size:18px;font-family:仿宋_GB2312;">6.</span><span style="font-size:18px;font-family:仿宋_GB2312;">积极参加学院感恩相关服务。</span></span></p><p style="text-indent:32pt;"><strong><span style="font-size:16pt;font-family:仿宋_GB2312;"><span style="font-size:18px;font-family:Calibri;">五、评选程序</span></span></strong></p><p style="text-indent:32pt;"><span style="font-family:Calibri;"><span style="font-size:18px;font-family:仿宋_GB2312;">1.</span><span style="font-size:18px;font-family:仿宋_GB2312;">学生申请。符合条件的学生，在学校规定的时间内，根据各学院具体安排，准备纸质版申请书（一式一份，使用华南农业大学抬头的<span style="font-size:18px;color:red;">横线信笺纸</span>手写，<span style="font-size:18px;">1000</span>字以上，内容包括家庭情况、在校表现、申请理由及自己的理想等，参见附件<span style="font-size:18px;">6</span>中申请书样式）、《仲明助学金申请表》（附件<span style="font-size:18px;">2</span>，一式两份，限<span style="font-size:18px;">A4</span>纸单面打印，以下简称《申请表》；同时，将《申请表》和《填表说明》使用一张<span style="font-size:18px;">A4</span>纸正反面打印）、个人身份证复印件（一式一份，身份证正反面内容使用<span style="font-size:18px;">A4</span>纸单面复印）、个人入学成绩单复印件（一式一份，此项为“仲明”创立人和管委会特别要求备存查核的资料）、学生家庭所在地街道或镇以上民政部门开具的家庭经济状况证明复印件（一式一份）、《仲明助学金道义契约》（附件<span style="font-size:18px;">3</span>，一式两份，限<span style="font-size:18px;">A4</span>纸单面打印）、《道义契约附件》（附件<span style="font-size:18px;">4</span>，一式两份，限<span style="font-size:18px;">A4</span>纸双面打印）；电子版《申请表》和《<span style="font-size:18px;">2015</span>学年仲明助学金拟资助学生名单汇总表》（附件<span style="font-size:18px;">5</span>）。</span></span></p><p style="text-indent:32pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;"><span style="font-size:18px;font-family:Calibri;">请申请学生将纸质版材料报送至学院指定地点，电子版材料发送到学院指定邮箱，向所在学院提出申请。</span></span></p><p style="text-indent:32pt;"><span style="font-family:Calibri;"><span style="font-size:16pt;font-family:仿宋_GB2312;"><span style="font-size:18px;">2．</span></span><span style="font-size:18px;font-family:仿宋_GB2312;">学院初审与公示。推荐学生名单将在学院范围内公示<span style="font-size:18px;">1</span>日。公示无异议后，报学生工作部（处）。</span></span></p><p><span style="font-family:Calibri;"><span style="font-size:18px;font-family:仿宋_GB2312;"><span style="font-size:18px;">&nbsp;&nbsp;&nbsp; </span>3</span><span style="font-size:18px;font-family:仿宋_GB2312;">．学校审核与公示。学校对各学院上报学生名单和材料进行审核，将拟推荐学生名单在全校范围内公示<span style="font-size:18px;">2</span>日。公示无异议后，将有关材料报资助方。</span></span></p><p><span style="font-family:Calibri;"><span style="font-size:18px;font-family:仿宋_GB2312;"><span style="font-size:18px;">&nbsp;&nbsp;&nbsp; </span>4</span><span style="font-size:18px;font-family:仿宋_GB2312;">．资助方评审。经资助方评审，确定最终获助学生名单，按规定程序和标准给予资助。</span></span></p><p><span style="font-family:Calibri;"><span style="font-size:16pt;font-family:仿宋_GB2312;"><span style="font-size:18px;">&nbsp;&nbsp; </span><strong><span style="font-size:18px;">&nbsp;</span></strong></span><strong><span style="font-size:18px;font-family:仿宋_GB2312;">六、报送材料</span></strong><span style="font-size:16pt;font-family:仿宋_GB2312;"></span></span></p><p style="text-indent:32pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;"><a href="mailto:%E8%AF%B7%E5%90%84%E5%AD%A6%E9%99%A2%E5%9C%A8%E8%A7%84%E5%AE%9A%E7%9A%84%E6%97%B6%E9%97%B4%E5%86%85%EF%BC%8C%E5%B0%86%E6%9D%90%E6%96%99%E7%BA%B8%E8%B4%A8%E7%89%88%E6%8A%A5%E9%80%81%E8%87%B3%E5%AD%A6%E6%A0%A1%E8%A1%8C%E6%94%BF%E5%A4%A7%E6%A5%BC227%E5%AE%A4%E5%AD%A6%E7%94%9F%E5%B7%A5%E4%BD%9C%E9%83%A8%EF%BC%88%E5%A4%84%EF%BC%89%E5%8A%A9%E5%AD%A6%E7%AE%A1%E7%90%86%E4%B8%AD%E5%BF%83%EF%BC%9B%E7%94%B5%E5%AD%90%E7%89%88%E5%8F%91%E9%80%81%E8%87%B3%E6%8C%87%E5%AE%9A%E9%82%AE%E7%AE%B1%EF%BC%88zxdk@scau.edu.cn%EF%BC%89%E3%80%82"><span style="color:windowtext;"><span style="font-size:18px;font-family:Calibri;">请各班在<span style="font-size:18px;">2015</span>年<span style="font-size:18px;">11</span>月<span style="font-size:18px;">9</span>日（星期一）中午<span style="font-size:18px;">12</span>点半到<span style="font-size:18px;">13</span>点之间将纸质版材料报送至泰山九栋楼下思源工作室的同学；电子版于<span style="font-size:18px;">11</span>月<span style="font-size:18px;">8</span>日（周日）晚上<span style="font-size:18px;">8</span>点前发送至指定邮箱：</span></span></a></span></p><p style="text-indent:32pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;"><span style="font-size:18px;font-family:Calibri;">scau_sxrsy_all@163.com</span></span></p><p style="text-indent:32pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;"><span style="font-size:18px;font-family:Calibri;">各学院报送材料清单如下：</span></span></p><p><span style="font-family:Calibri;"><span style="font-size:16pt;font-family:仿宋_GB2312;"><span style="font-size:18px;">&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style="font-size:18px;font-family:仿宋_GB2312;">（一）纸质版材料</span></span></p><p style="text-indent:32pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;"><span style="font-size:18px;font-family:Calibri;">每\r\n位申请学生的纸质版申请书（一式一份 \r\n）、《申请表》（含《填表说明》）、个人身份证复印件（一式一份）、个人入学成绩单复印件（一式一份）、学生家庭所在地街道或镇以上民政部门开具的家庭经\r\n济状况证明复印件（一式一份）、《仲明助学金道义契约》（一式两份）、《道义契约附件》（一式两份）。</span></span></p><p style="text-indent:32pt;"><span style="font-family:Calibri;"><span style="font-size:18px;font-family:仿宋_GB2312;color:red;">请将每位申请学生的材料按上述顺序排列，左侧使用回形针扣住（请勿装订）</span><span style="font-size:18px;font-family:仿宋_GB2312;">。</span></span></p><p style="text-indent:32pt;"><span style="font-size:16pt;font-family:仿宋_GB2312;"><span style="font-size:18px;font-family:Calibri;">（二）电子版材料</span></span></p><p><span style="font-family:Calibri;"><span style="font-size:16pt;font-family:仿宋_GB2312;"><span style="font-size:18px;">&nbsp;&nbsp; </span></span><span style="font-size:18px;font-family:仿宋_GB2312;">《申请表》（文件统一命名为<span style="font-size:18px;">xx</span>专业<span style="font-size:18px;">xx</span>班<span style="font-size:18px;">xx</span>同学仲明助学金申请表）</span></span></p><p><span style="font-size:16pt;font-family:仿宋_GB2312;"><span style="font-size:18px;font-family:Calibri;">、《<span style="font-size:18px;">2015</span>学年仲明助学金拟资助学生名单汇总表》以班级为单位，<a href="mailto:%E7%94%B1%E7%94%9F%E5%A7%94%E5%8F%91%E5%88%B0scau_sxrsy_all@163.com"><span style="font-size:18px;color:#333333;">由<span style="font-size:18px;">生委发到scau_sxrsy_all@163.com</span></span></a>。</span></span></p><p><span style="font-size:16pt;font-family:仿宋_GB2312;"><span style="font-size:18px;font-family:Calibri;">（邮件统一命名为<span style="font-size:18px;">xx</span>班仲明助学金材料）</span></span></p><p><span style="font-family:Calibri;"><span style="font-size:16pt;font-family:仿宋_GB2312;"><span style="font-size:18px;">&nbsp;&nbsp;&nbsp; </span></span><strong><span style="font-size:18px;font-family:仿宋_GB2312;">七、工作要求</span></strong></span></p><p style="text-indent:32pt;"><span style="font-family:Calibri;"><span style="font-size:16pt;font-family:仿宋_GB2312;"><span style="font-size:18px;">1.</span></span><span style="font-size:18px;font-family:仿宋_GB2312;">学院将高度重视，严格规范评审程序，严格把握评审标准，在家庭经济困难、学习成绩并认同仲明理念方面把好关，切实评选出符合条件的学生。</span></span></p><p><span style="font-family:Calibri;"><span style="font-size:18px;font-family:仿宋_GB2312;"><span style="font-size:18px;">&nbsp;&nbsp;&nbsp; </span>2.</span><span style="font-size:18px;font-family:仿宋_GB2312;">各学院在评选工作中，要坚持把培植先进典型、发挥模范引领的标杆作用作为评选工作的重要内容。同时，对资助学生开展感恩、励志、诚信等教育，营造良好的资助育人氛围。</span></span></p><p><span style="font-family:Calibri;"><span style="font-size:18px;font-family:仿宋_GB2312;"><span style="font-size:18px;">&nbsp;&nbsp;&nbsp; </span>3.</span><span style="font-size:18px;font-family:仿宋_GB2312;">根据资助方的要求，学生在自愿申请仲明助学金的同时，签署《仲明助学金道义契约》，承诺毕业后将受助的款项回捐到仲明基金用以帮助更多有需要的贫困学子，并身体力行加入助困的行列。践行“受惠社会，回报社会”的公益精神。</span></span></p><p style="text-indent:32pt;"><span style="font-family:Calibri;"><span style="font-size:18px;font-family:仿宋_GB2312;">4.</span><span style="font-size:18px;font-family:仿宋_GB2312;">评审中或学生受助后，如发现有违法违纪行为或弄虚作假，一经核实，取消受助资格，收回助学金，并按学校有关规定处理。</span></span></p><p style="text-indent:32pt;"><span style="font-family:Calibri;"><span style="font-size:18px;font-family:仿宋_GB2312;">5.</span><span style="font-size:18px;font-family:仿宋_GB2312;">同时，根据评选要求和时间节点，按照附件<span style="font-size:18px;">2-5</span>填报要求，按时做好材料报送工作。</span></span></p><p style="text-indent:32pt;"><span style="font-family:Calibri;"><span style="font-size:18px;font-family:仿宋_GB2312;">6. 2015</span><span style="font-size:18px;font-family:仿宋_GB2312;">年“仲明助学金”颁发仪式初定于<span style="font-size:18px;">11</span>月下旬，具体时间地点另文通知。</span></span></p><p>&nbsp;</p><p><span style="font-size:16pt;font-family:仿宋_GB2312;"><span style="font-family:Calibri;"><span style="font-size:18px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span></p><p><span style="font-family:Calibri;"><span style="font-size:16pt;font-family:仿宋_GB2312;"><span style="font-size:18px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style="font-size:18px;">&nbsp;</span><span style="font-size:18px;">&nbsp;&nbsp;&nbsp;</span></span><span style="font-size:18px;font-family:仿宋_GB2312;">数学与信息学院 软件学院</span></span></p><p><span style="font-family:Calibri;"><span style="font-size:16pt;font-family:仿宋_GB2312;"><span style="font-size:18px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style="font-size:18px;font-family:仿宋_GB2312;">二零一五年十一月六日</span></span></p><p><br/></p>', 37);
INSERT INTO `sy_article` (`id`, `subject`, `lastmodifytime`, `author`, `message`, `createtime`) VALUES
(20, '关于我院第二届本科生“模范引领计划”的答辩通知', 1464854771, '思源宣传部', '<p><span style="font-family:宋体"><span style="font-size:18px">各年级各班：</span></span></p><p><span style="font-size:small"><span style="font-size:18px">&nbsp;&nbsp;&nbsp;&nbsp; </span><span style="font-size:18px;font-family:宋体">根据学校的统一安排和要求，在优中选优的原则下，经个人申请，学院“模范引领计划”工作领导小组初审，现将获得答辩资格的学生名单进行公示（具体名单如下，公示时间为</span><span style="font-size:18px">2015</span><span style="font-size:18px;font-family:宋体">年</span><span style="font-size:18px">11</span><span style="font-size:18px;font-family:宋体">月</span><span style="font-size:18px">9</span><span style="font-size:18px;font-family:宋体">日至</span><span style="font-size:18px">10</span><span style="font-size:18px;font-family:宋体">日。如有异议，请在公示期工作时间内通过电话或书面形式向年级辅导员反映。反映情况时要自报或签署真实姓名，不报或不签署真实姓名的，一律不予受理。</span></span></p><p><span style="font-size:small"><span style="font-size:18px;font-family:宋体">联系电话：</span><span style="font-size:18px">38295704 </span><span style="font-size:18px;font-family:宋体">或</span><span style="font-size:18px">85283315</span></span></p><p><span style="font-size:small"> </span></p><table height="513" width="634"><colgroup><col width="143"/><col width="72"/><col width="181"/><col width="238"/></colgroup><tbody><tr class="firstRow" height="19"><td style="font-size: 16px; border-width: 1px; border-style: solid; border-color: rgb(0, 0, 0); vertical-align: middle; background: rgb(231, 230, 230) none repeat scroll 0% 0%; font-weight: 400; color: rgb(0, 0, 0); font-style: normal; text-align: center;" height="19" width="143">陈媛</td><td style="font-size: 16px; border-top: 1px solid rgb(0, 0, 0); border-right: 1px solid rgb(0, 0, 0); vertical-align: middle; background: rgb(231, 230, 230) none repeat scroll 0% 0%; font-weight: 400; color: rgb(0, 0, 0); font-style: normal; text-align: center; border-left: 1px solid rgb(0, 0, 0);" height="19" width="72">2014</td><td style="font-size: 16px; border-width: 1px; border-style: solid; border-color: rgb(0, 0, 0); vertical-align: middle; backgr