INSERT INTO `sy_essay` (`id`, `tag`, `brief_introduction`, `picture_url`, `createTime`, `updateTime`, `author`, `subject`, `title`, `body`) VALUES
(3, '采访', '尹国龙，数学与信息学院（软件学院）2012级信息与计算科学学生。第二届模范引领计划数学与信息学院创业之星获得者。SYB学员，曾创立旺途思瑞软件外包工作室，现任广州新燃点网络科技有限公司CEO。', 'http://7xlp0l.com1.z0.glb.clouddn.com/146509522600059.png', 1465095484, 1465095484, '尹国龙', '模范引领', '【模范引领】创业维艰，奋斗以成', '<section class="yead_editor" label="Copyright © 2015 Yead All Rights Reserved." style=""><section style="margin:0;padding:0;max-width:100%;box-sizing:border-box;border:0 none;font-size:14px;font-family:微软雅黑;word-wrap:break-word!important"><section style="margin:10px auto;padding:0;max-width:100%;box-sizing:border-box;border:1px solid #c5c8cc;text-align:center;border-radius:5px;box-shadow:#fff 0 1px 0,#c5c8cc 0 2px 0,#fff 0 3px 0,#c5c8cc 0 4px 0;word-wrap:break-word!important"><section style="margin:0;padding:0;max-width:100%;box-sizing:border-box;height:25px;border-bottom-width:0;border-bottom-style:solid;border-bottom-color:#c5c8cc;border-radius:4px 4px 0 0;word-wrap:break-word!important;background-color:#dbdbdd"><section style="margin:0 100px 0 0;padding:0;max-width:100%;box-sizing:border-box;display:inline-block;height:14px;width:14px;border-radius:7px;word-wrap:break-word!important;background-color:#adadad"><section style="margin:30px 0 0;padding:0;max-width:100%;box-sizing:border-box;height:14px;width:14px;border-radius:7px;word-wrap:break-word!important;background-color:#dedede"></section><section style="margin:-37px 0 0 2px;padding:0;max-width:100%;box-sizing:border-box;width:8px;height:30px;border:1px solid #cacaca;border-radius:3px;word-wrap:break-word!important;background-color:#fff"></section></section><section style="margin:0;padding:0;max-width:100%;box-sizing:border-box;display:inline-block;height:14px;width:14px;border-radius:7px;word-wrap:break-word!important;background-color:#adadad"><section style="margin:30px 0 0;padding:0;max-width:100%;box-sizing:border-box;height:14px;width:14px;border-radius:7px;word-wrap:break-word!important;background-color:#dedede"></section><section style="margin:-37px 0 0 2px;padding:0;max-width:100%;box-sizing:border-box;width:8px;height:30px;border:1px solid #cacaca;border-radius:3px;word-wrap:break-word!important;background-color:#fff"></section></section></section><section style="margin:30px 10px;padding:0;max-width:100%;box-sizing:border-box;word-wrap:break-word!important"><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; box-sizing: border-box; clear: both; min-height: 1em; line-height: 30px; text-align: center; word-wrap: break-word ! important; text-indent: 2em;"><span style="font-family: 楷体,楷体_GB2312,SimKai; font-size: 16px; color: rgb(149, 55, 52);">数学与信息（软件）学院</span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; box-sizing: border-box; clear: both; min-height: 1em; line-height: 30px; word-wrap: break-word ! important; text-indent: 2em;"><span style="font-family: 楷体,楷体_GB2312,SimKai; font-size: 18px; color: rgb(192, 0, 0);">模范引领计划创业之星获得者</span></p><p style="text-align: center;"><span style="font-family: 楷体,楷体_GB2312,SimKai; font-size: 20px; color: rgb(255, 0, 0);">尹国龙</span></p></section></section></section></section><p><section class="yead_editor" label="Copyright © 2015 Yead All Rights Reserved." style=""><section style="margin:10px 0;padding:0;text-align:center;font-family:微软雅黑"><section style="margin:0;padding:0;text-align:center;transform:rotate(0);-moz-transform:rotate(0);-ms-transform:rotate(0);-o-transform:rotate(0);-webkit-transform:rotate(0);display:inline-block"><section style="margin:0 auto;padding:0;width:25px;height:25px;background-color:#fff;border-radius:50%;text-align:center;display:table-cell;vertical-align:middle"><span class="yead_color" style="color: rgb(58, 188, 255); font-size: 18px;">★</span></section></section><section class="yead_bdtc yead_bdbc" style="margin: -16px 0px; padding: 20px 10px; border-top: 1px solid rgb(58, 188, 255); border-bottom: 1px solid rgb(58, 188, 255); color: rgb(51, 51, 50);"><p style="margin:0;padding:0;word-wrap:break-word;color:inherit;font-size:14px;text-align:justify;line-height:30px;text-indent:2em"><span style="color:inherit"></span></p><p style="text-align: left;"><span style=";font-family:微软雅黑;font-size:14px"> &nbsp; &nbsp; &nbsp; <span style="font-family: 微软雅黑; font-size: 18px;">尹国龙，数学与信息学院（软件学院）2012级信息与计算科学学生。第二届模范引领计划数学与信息学院创业之星获得者。SYB学员，曾创立旺途思瑞软件外包工作室，现任广州新燃点网络科技有限公司CEO。</span></span></p><p style="text-align: left;"><span style="font-family: 微软雅黑; font-size: 18px;">人生格言：以诚待人者，人亦诚而应。</span></p><p style="text-align: left;"><span style=";font-family:微软雅黑;font-size:14px"><img data-w="437" data-ratio="0.8489702517162472" data-s="300,640" data-type="png" src="http://7xlp0l.com1.z0.glb.clouddn.com/14650953879.png"/><br/></span></p><p style="text-indent: 28px; text-align: left;"><span style="font-family: 微软雅黑; font-size: 18px;">他\r\n带领旺途思瑞工作室期间，曾是华南农业大学创业孵化基地最勤奋的团队，得到老师们的一致好评。旺途思瑞工作室先后为数家创业公司提供过服务，同时也为学校\r\n相关部门和学院制作过网站。为了工作室成员的发展，他和中山某企业合作，让工作室在技术上服务于该企业，同时提供给成员们更好的成长机会。现在，他又和几\r\n个志同道合的伙伴一起成立了广州新燃点网络科技有限公司，该公司得到了学校和广东省领导的赞赏与支持。</span></p><p style="text-indent: 28px; text-align: left;"><span style="font-family: 微软雅黑; font-size: 18px;">他曾经为了项目敲过校长办公室门，以普通学生身份参加过学校学生会主席例会，赞助过资环学院院运会，睡过大街，面临过各种问题。困难没有吓跑他，反而让他更加坚韧，不断成长。</span></p><p style="text-indent: 28px; text-align: left;"><span style="font-family: 微软雅黑; font-size: 18px;">创\r\n业过程中，他始终坚信以诚待人者，人亦诚而应，所以他收获了数位不离不弃，肝胆相照的创业伙伴。他说，人一旦发愁，上帝就发笑，所以他一直保持着乐观的态\r\n度面对各种各样的挫折。他是一个不断学习的人，阅读了大量书籍，通过自学掌握了PS、AI、Office、Axure、X-mind等实用工具，同时掌握\r\n了个人知识管理，时间管理，数据挖掘等有效方法。在艰苦创业的过程中，他仍然保持着规律的作息时间和每天晚上跑步的习惯。他说创业就像长跑，懂得坚持的人\r\n才能到达终点。</span></p><p style="text-indent: 28px; text-align: center;"><br/><img style="width: 709px; height: 883px;" data-w="536" data-ratio="1.382462686567164" data-s="300,640" data-type="png" src="http://7xlp0l.com1.z0.glb.clouddn.com/14650953873.png" height="883" width="709"/><br/></p><p style="text-indent: 28px;">小彩蛋：偷偷卖个萌<br/><span style=";font-family:微软雅黑;font-size:14px"></span></p><p style="text-indent: 28px; text-align: left;"><span style=";font-family:微软雅黑;font-size:14px"></span></p></section><section style="margin:0;padding:0;text-align:center;transform:rotate(0);-moz-transform:rotate(0);-ms-transform:rotate(0);-o-transform:rotate(0);-webkit-transform:rotate(0);display:inline-block"><section style="margin:0 auto;padding:0;width:25px;height:25px;background-color:#fff;border-radius:50%;text-align:center;display:table-cell;vertical-align:middle"><span class="yead_color" style="color: rgb(58, 188, 255); font-size: 18px;">★</span></section></section></section></section></p><p><br/></p><p><br/></p>'),
(4, '采访', '徐禹洪，数学与信息学院13级软件工程专业学生。第二届模范引领计划数学与信息学院学术之星获得者。曾于数学与信息学院团委网络部工作，现任班级团支书。', 'http://7xlp0l.com1.z0.glb.clouddn.com/146509552600041.png', 1465095719, 1465095719, '徐禹洪', '模范引领', '努力造就实力，态度决定高度', '<section class="yead_editor" style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;" label="Copyright © 2015 Yead All Rights Reserved."><section style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box; word-wrap: break-word !important; border: 0px none; font-size: 14px; font-family: 微软雅黑;"><section style="margin: 10px auto; padding: 0px; max-width: 100%; box-sizing: border-box; word-wrap: break-word !important; border: 1px solid rgb(197, 200, 204); text-align: center; border-radius: 5px; box-shadow: rgb(255, 255, 255) 0px 1px 0px, rgb(197, 200, 204) 0px 2px 0px, rgb(255, 255, 255) 0px 3px 0px, rgb(197, 200, 204) 0px 4px 0px;"><section style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box; word-wrap: break-word !important; height: 25px; border-bottom-width: 0px; border-bottom-style: solid; border-bottom-color: rgb(197, 200, 204); border-radius: 4px 4px 0px 0px; background-color: rgb(219, 219, 221);"><section style="margin: 0px 100px 0px 0px; padding: 0px; max-width: 100%; box-sizing: border-box; word-wrap: break-word !important; display: inline-block; height: 14px; width: 14px; border-radius: 7px; background-color: rgb(173, 173, 173);"><section style="margin: 30px 0px 0px; padding: 0px; max-width: 100%; box-sizing: border-box; word-wrap: break-word !important; height: 14px; width: 14px; border-radius: 7px; background-color: rgb(222, 222, 222);"></section><section style="margin: -37px 0px 0px 2px; padding: 0px; max-width: 100%; box-sizing: border-box; word-wrap: break-word !important; width: 8px; height: 30px; border: 1px solid rgb(202, 202, 202); border-radius: 3px; background-color: rgb(255, 255, 255);"></section></section><section style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box; word-wrap: break-word !important; display: inline-block; height: 14px; width: 14px; border-radius: 7px; background-color: rgb(173, 173, 173);"><section style="margin: 30px 0px 0px; padding: 0px; max-width: 100%; box-sizing: border-box; word-wrap: break-word !important; height: 14px; width: 14px; border-radius: 7px; background-color: rgb(222, 222, 222);"></section><section style="margin: -37px 0px 0px 2px; padding: 0px; max-width: 100%; box-sizing: border-box; word-wrap: break-word !important; width: 8px; height: 30px; border: 1px solid rgb(202, 202, 202); border-radius: 3px; background-color: rgb(255, 255, 255);"></section></section></section><section style="margin: 30px 10px; padding: 0px; max-width: 100%; box-sizing: border-box; word-wrap: break-word !important;"><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; box-sizing: border-box; min-height: 1em; white-space: pre-wrap; line-height: 30px; text-indent: 2em; word-wrap: break-word !important;"><span style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-family: 楷体, 楷体_GB2312, SimKai; font-size: 16px; color: rgb(149, 55, 52);">数学与信息（软件）学院</span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; box-sizing: border-box; min-height: 1em; white-space: pre-wrap; line-height: 30px; text-indent: 2em; word-wrap: break-word !important;"><span style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-family: 楷体, 楷体_GB2312, SimKai; font-size: 18px; color: rgb(192, 0, 0);">模范引领计划学术之星获得者</span></p><p><span style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-family: 楷体, 楷体_GB2312, SimKai; font-size: 20px; color: rgb(255, 0, 0);">徐禹洪</span></p></section></section></section></section><p><section class="yead_editor" style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;" label="Copyright © 2015 Yead All Rights Reserved."><section style="margin: 10px 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; text-align: center;"><section style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-family: 微软雅黑; transform: rotate(0deg); display: inline-block;"><section style="margin: 0px auto; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; width: 25px; height: 25px; border-radius: 50%; display: table-cell; vertical-align: middle; background-color: rgb(255, 255, 255);"><span class="yead_color" style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; color: rgb(58, 188, 255); font-size: 18px;">★</span></section></section><section class="yead_bdtc yead_bdbc" style="margin: -16px 0px; padding: 20px 10px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; border-top-width: 1px; border-top-style: solid; border-top-color: rgb(58, 188, 255); border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(58, 188, 255);"><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; word-wrap: break-word; min-height: 1em; white-space: pre-wrap; color: inherit; font-family: 微软雅黑; font-size: 14px; text-align: justify; line-height: 30px; text-indent: 2em; box-sizing: border-box !important;"><span style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; color: inherit;"></span></p><p style="margin-bottom: 17px; text-indent: 28px; line-height: 21px; text-align: left;"><span style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 18px;">徐禹洪，数学与信息学院13级软件工程专业学生。第二届模范引领计划数学与信息学院学术之星获得者。曾于数学与信息学院团委网络部工作，现任班级团支书。</span></p><p style="margin-top: 4px; line-height: 200%; text-indent: 0em; text-align: center;"><span style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 18px;"></span><img src="http://7xlp0l.com1.z0.glb.clouddn.com/14650956091.png" data-type="png" data-s="300,640" data-ratio="1.663265306122449" data-w="490"/><br/><span style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 18px;">发表学术论文及参与创新实践项目如下：</span></p><p style="text-indent: 0em; text-align: left;"><span style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 18px;">1、限定领域口语对话系统中超出领域话语的协处理方法. 中文信息学报, 2015 &nbsp;(中文信息处理领域的权威期刊)；</span></p><p style="text-indent: 0em; text-align: left;"><span style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 18px;">2、\r\nChinese Grammatical Error Diagnosis System Based on Hybrid Model. \r\nProceedings of The 2nd Workshop on Natural Language Processing \r\nTechniques for Educational Applications, pages 117–125, Beijing, China, \r\nJuly 31, 2015. (顶级会议ACL的workshop)；</span></p><p style="text-indent: 0em; text-align: left;"><span style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 18px;">3、2015年广东省大学生创新训练计划项目“口语对话系统中对话管理的研究及应用”，编号：201510564281，项目负责人；</span></p><p style="text-indent: 0em; text-align: left;"><span style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 18px;">4、2014年广东省大学生创新训练计划项目“中文口语对话系统中问句表达多样性研究及应用”，编号：201410564290，项目核心成员；</span></p><p style="text-indent: 0em; text-align: left;"><span style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 18px;">5、2013年院级大学生创新训练计划项目“基于AIML的手机导购领域对话协处理系统”，项目核心成员，并获得“优秀结题”称号。</span></p><p style="text-indent: 0em; text-align: left;"><span style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 18px;"><br/></span></p><p style="line-height: 200%; text-indent: 0em; text-align: left;"><span style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 18px;">专业竞赛获奖情况：</span></p><p style="text-indent: 0em; text-align: left;"><span style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 18px;">1、2015年，全国“甲骨文杯”java竞赛华南赛区二等奖；</span></p><p style="text-indent: 0em; text-align: left;"><span style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 18px;">2、第十三届“挑战杯”广东省大学生课外学术科技竞赛二等奖；</span></p><p style="text-indent: 0em; text-align: left;"><span style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 18px;">3、国际中文语法纠错比赛（NLP-TEA 2015 Shared Task for Chinese Grammatical Error Diagnosis）,第5名；</span></p><p style="text-indent: 0em; text-align: left;"><span style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 18px;">4、2014年，“丁颖杯”课外学术科技竞赛二等奖；</span></p><p style="text-indent: 0em; text-align: left;"><span style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 18px;">5、2014年，“远标杯”C语言技能竞赛优胜奖；</span></p><p style="text-indent: 0em; text-align: left;"><span style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 18px;">6、曾获得校级二等奖学金、三等奖学金；</span></p><p style="text-indent: 0em; text-align: left;"><span style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 18px;">7、2014-2015年度校级阳光体育先进个人。</span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; min-height: 1em; white-space: pre-wrap; color: rgb(51, 51, 50); font-family: 微软雅黑; text-align: left; box-sizing: border-box !important; word-wrap: break-word !important;"><span style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 18px;"></span></p><p style="margin-bottom: 17px; line-height: 21px; text-align: left; text-indent: 0em;"><span style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 18px; text-indent: 28px; line-height: 25.6px;">人生格言：心之所向，无所不能。</span></p><p style="margin-bottom: 17px; line-height: 21px; text-align: center; text-indent: 0em;"><span style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 18px; text-indent: 28px; line-height: 25.6px;"><img src="http://7xlp0l.com1.z0.glb.clouddn.com/14650956092.png" data-type="jpeg" data-s="300,640" data-ratio="1.373469387755102" data-w="490"/><br/></span></p><p style="margin-bottom: 17px; line-height: 21px; text-align: left; text-indent: 2em;"><span style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 18px;">\r\n 他开拓进取、积极向上、视野开阔。学习认真，各科成绩优秀，平均绩点4.32。积极参与科研实践，主持和参与省级大学生创新训练计划项目各1项。在《中文\r\n信息学报》和ACL（顶级国际会议）的workshop上发表论文各1篇。积极参加学科专业竞赛并获奖。他还积极参与一些学术会议，了解最前沿的研究方向\r\n与成果。</span></p><p style="margin-bottom: 17px; line-height: 21px; text-align: left; text-indent: 2em;"><span style="margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; font-size: 18px;">他性格开朗，乐于助人，朴素节俭，崇尚科学，追求自身德、智、体、美的全面发展。积极参加各种征文比赛，如求索工作室征文、华农绿窗文学社征文等，以丰富自己的课余文化生活，陶冶情操。热爱运动的他也积极组织和参与院运动会与阳光体育运动。<br/></span></p></section></section></section></p><p><br/></p><p><br/></p>'),
(5, '采访', '王泳欣，数学与信息学院2014级信息管理与信息系统专业学生。第二届模范引领计划数学与信息学院自强之星获得者。', 'http://7xlp0l.com1.z0.glb.clouddn.com/1465095682000213.png', 1465095841, 1465095841, '王泳欣', '模范引领', '积一时之跬步，臻千里之遥程', '<section class="yead_editor" label="Copyright © 2015 Yead All Rights Reserved." style=""><section style="max-width: 100%; box-sizing: border-box; border: 0px none; font-family: 微软雅黑; word-wrap: break-word !important;"><section style="margin: 10px auto; max-width: 100%; box-sizing: border-box; border: 1px solid rgb(197, 200, 204); text-align: center; border-top-left-radius: 5px; border-top-right-radius: 5px; border-bottom-right-radius: 5px; border-bottom-left-radius: 5px; box-shadow: rgb(255, 255, 255) 0px 1px 0px, rgb(197, 200, 204) 0px 2px 0px, rgb(255, 255, 255) 0px 3px 0px, rgb(197, 200, 204) 0px 4px 0px; word-wrap: break-word !important;"><section style="max-width: 100%; box-sizing: border-box; height: 25px; border-bottom-width: 0px; border-bottom-style: solid; border-bottom-color: rgb(197, 200, 204); border-top-left-radius: 4px; border-top-right-radius: 4px; border-bottom-right-radius: 0px; border-bottom-left-radius: 0px; background-color: rgb(219, 219, 221); word-wrap: break-word !important;"><section style="margin-right: 100px; max-width: 100%; box-sizing: border-box; display: inline-block; height: 14px; width: 14px; border-top-left-radius: 7px; border-top-right-radius: 7px; border-bottom-right-radius: 7px; border-bottom-left-radius: 7px; background-color: rgb(173, 173, 173); word-wrap: break-word !important;"><section style="margin-top: 30px; max-width: 100%; box-sizing: border-box; height: 14px; width: 14px; border-top-left-radius: 7px; border-top-right-radius: 7px; border-bottom-right-radius: 7px; border-bottom-left-radius: 7px; background-color: rgb(222, 222, 222); word-wrap: break-word !important;"></section><section style="margin-top: -37px; margin-left: 2px; max-width: 100%; box-sizing: border-box; width: 8px; height: 30px; border: 1px solid rgb(202, 202, 202); border-top-left-radius: 3px; border-top-right-radius: 3px; border-bottom-right-radius: 3px; border-bottom-left-radius: 3px; background-color: rgb(255, 255, 255); word-wrap: break-word !important;"></section></section><section style="max-width: 100%; box-sizing: border-box; display: inline-block; height: 14px; width: 14px; border-top-left-radius: 7px; border-top-right-radius: 7px; border-bottom-right-radius: 7px; border-bottom-left-radius: 7px; background-color: rgb(173, 173, 173); word-wrap: break-word !important;"><section style="margin-top: 30px; max-width: 100%; box-sizing: border-box; height: 14px; width: 14px; border-top-left-radius: 7px; border-top-right-radius: 7px; border-bottom-right-radius: 7px; border-bottom-left-radius: 7px; background-color: rgb(222, 222, 222); word-wrap: break-word !important;"></section><section style="margin-top: -37px; margin-left: 2px; max-width: 100%; box-sizing: border-box; width: 8px; height: 30px; border: 1px solid rgb(202, 202, 202); border-top-left-radius: 3px; border-top-right-radius: 3px; border-bottom-right-radius: 3px; border-bottom-left-radius: 3px; background-color: rgb(255, 255, 255); word-wrap: break-word !important;"></section></section></section><section style="margin: 30px 10px; max-width: 100%; box-sizing: border-box; word-wrap: break-word !important;"><p style=""><span style="color: rgb(146, 208, 80);"><span style="color: rgb(227, 108, 9);"><span style="font-family: 宋体;"> 数学与信息（软件）</span><span style="font-family: Calibri;">学院</span></span><span style="font-family: Calibri;"> &nbsp;</span></span></p><p style=""><span style="color: rgb(255, 0, 0); font-family: 宋体; line-height: 1.6;">模范引领计划自强之星获得者</span></p><p style=""><span style="color: rgb(255, 0, 0); font-family: 宋体; line-height: 1.6;">王泳欣<br/></span></p><p><br/></p></section></section></section></section><p><br/></p><p><section class="yead_editor" label="Copyright © 2015 Yead All Rights Reserved." style="margin: 5px auto; white-space: normal;"><section style="margin-top: 10px; margin-bottom: 10px; text-align: center;"><section style="font-size: 14px; font-family: 微软雅黑; -webkit-transform: rotate(0deg); display: inline-block;"><section style="margin-right: auto; margin-left: auto; width: 25px; height: 25px; background-color: rgb(255, 255, 255); border-top-left-radius: 50%; border-top-right-radius: 50%; border-bottom-right-radius: 50%; border-bottom-left-radius: 50%; display: table-cell; vertical-align: middle;"><span class="yead_color" style="color: rgb(58, 188, 255); font-size: 18px;">★</span></section></section><section class="yead_bdtc yead_bdbc" style="margin-top: -16px; margin-bottom: -16px; padding: 20px 10px; border-top-width: 1px; border-top-style: solid; border-top-color: rgb(58, 188, 255); border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(58, 188, 255); color: rgb(51, 51, 50);"><p style="font-size: 14px; font-family: 微软雅黑; word-wrap: break-word; color: inherit; text-align: justify; line-height: 30px; text-indent: 2em;"><span style="color:inherit"></span></p><p style="text-align: left;"><span style="font-size: 18px;">王泳欣，数学与信息学院2014级信息管理与信息系统专业学生。第二届模范引领计划数学与信息学院自强之星获得者。曾任数学与信息学院学生会年级管理委员会干事和班内宣传委员，现任年级管理委员会部长。</span></p><p style="font-size: 14px; margin-top: 7px; margin-bottom: 17px; text-indent: 28px; line-height: 150%;"><span style="font-size: 24px; font-family: Calibri;"><img style="float: left;" data-w="533" data-ratio="1.5590994371482176" data-s="300,640" data-type="png" src="http://7xlp0l.com1.z0.glb.clouddn.com/14650957635.png"/><br/></span></p><p style="margin-left:0;text-indent:0;text-align:left;line-height:150%"><span style="font-size: 20px;"><span style="color: rgb(255, 79, 121); font-family: 宋体;">人生格言：</span><span style="font-size: 20px; font-family: 宋体; color: rgb(255, 79, 121);">世上无难事，只怕有心人。</span></span></p><p style="font-size: 14px; margin-top: 7px; margin-bottom: 17px; text-indent: 28px; line-height: 150%;"><span style="font-size: 24px; font-family: Calibri;"></span></p><p style="text-align: left;"><span style="font-size: 20px; color: rgb(255, 41, 65);">获奖情况：</span></p><p style="text-align: left;"><span style="font-size: 20px; font-family: 宋体, SimSun;"></span>1、2014-2015年度国奖奖学金</p><p style="text-align: left;">2、2014-2015年华南农业大学院际赛女子篮球第五名</p><p style="text-align: left;">3、2014-2015学年度华南农业大学阳光体育先进个人</p><p style="text-align: left;">4、2014-2015年2014级学生军事训练获优秀学生干部称号</p><p style="text-align: left;">5、2014-2015年度阳光体育先进个人杰出贡献奖</p><p style="text-align: left;">6、2014-2015年度院级优秀共青团员标兵称号</p><p style="text-align: left;">7、2014-2015年度院团委学生会优秀干事</p><p style="text-align: left;">8、2014-2015年数学与信息学院、软件学院第十二届田径运动会女子三级跳远亚军</p><p style="text-align: left;">9、2014-2015年数学与信息学院、软件学院优秀学生干部称号</p><p style="font-size: 14px; margin-top: 7px; margin-bottom: 17px; text-indent: 28px; line-height: 16px; text-align: left;"><span style="font-family: 宋体,SimSun; font-size: 18px;"></span></p><p style="text-align:center"><img style="width: 749px; height: 666px;" data-w="536" data-ratio="0.75" data-s="300,640" data-type="png" src="http://7xlp0l.com1.z0.glb.clouddn.com/14650957640.png" height="666" width="749"/></p><p style="text-align: left;"><span style="font-size: 18px;"><span style="font-family: 宋体;"> &nbsp; &nbsp; </span>她在班内学习拔尖，学习刻苦认真，同时在各方面表现也很出色。</span></p><p style="text-align: left;"><span style="font-size: 18px;">\r\n &nbsp; &nbsp; &nbsp; &nbsp; \r\n担任宣传委员期间，她设计了我们班独具特色的班徽和班服，并且积极参与班内活动的组织，活跃了班内的气氛。担任年级管理委员会干事期间，她认真负责，获得\r\n了2014级军事训练中获得优秀学生干部和优秀干事等称号；同时她也积极参与并组织部门活动，使大家在工作之余有一个温馨舒适的工作环境。</span></p><p style="text-align: left;"><span style="font-size: 18px;">\r\n &nbsp; &nbsp; &nbsp; &nbsp; \r\n她积极参加体育活动。她加入了学院的女子篮球队，每一天她都坚持去训练，即使再累也不放弃，因此她和队友一起在院际篮球赛中获得好名次。同时她积极参加院\r\n运会和校运会，并在院运会中获得三级跳远第二名的好成绩。此外，她还当好了学生干部模范带头作用，积极参加送旧杯、新生杯等比赛，激发大家参加比赛的热\r\n情。</span></p><p style="text-align: left;"><span style="font-size: 18px;"> &nbsp; &nbsp; &nbsp; &nbsp;她热心公益，积极参加社会实践，努力提升综合素养。她曾参加暑期“三下乡”和旧书回收活动，给山区的孩子带去温暖。她懂得感恩，懂得付出，乐于助人，是一个自立自强的学生。</span></p></section><section style="font-size: 14px; font-family: 微软雅黑; -webkit-transform: rotate(0deg); display: inline-block;"><section style="margin-right: auto; margin-left: auto; width: 25px; height: 25px; background-color: rgb(255, 255, 255); border-top-left-radius: 50%; border-top-right-radius: 50%; border-bottom-right-radius: 50%; border-bottom-left-radius: 50%; display: table-cell; vertical-align: middle;"><span class="yead_color" style="color: rgb(58, 188, 255); font-size: 18px;">★</span></section></section></section></section><section class="yead_editor" label="Copyright © 2015 Yead All Rights Reserved." style=""><section style="border: none rgb(255, 129, 36); margin-top: 0.5em; margin-bottom: 0.5em; font-family: inherit; font-weight: inherit; text-decoration: inherit; color: rgb(255, 129, 36); box-sizing: border-box;"><section class="yead_editor" label="Copyright © 2015 Yead All Rights Reserved." style="margin: 5px auto;"><section class="yead_bdlc" style="color: inherit; font-size: 16px; padding-right: 10px; padding-left: 35px; margin-left: 27px; border-left-width: 1px; border-left-style: dotted; border-left-color: rgb(58, 188, 255);"><section class="yead_bgc" style="width: 26px; height: 26px; margin-left: -50px; color: rgb(255, 255, 255); text-align: center; border-top-left-radius: 16px; border-top-right-radius: 16px; border-bottom-right-radius: 16px; border-bottom-left-radius: 16px; background-color: rgb(58, 188, 255); background-position: initial initial; background-repeat: initial initial;">一</section><section style="margin-top:-26px;color:inherit;clear:both;line-height:30px;font-size:14px;color:inherit"><p><span style="font-family: 宋体, SimSun;"> 图文来源：王泳欣</span></p><p><span style="font-family: 宋体, SimSun;"> 编辑：思源工作室 陈嘉兴<br/></span></p></section></section></section></section></section></p><p><br/></p>'),
(6, '采访', '唐雪妹，数学与信息（软件）学院2013级统计学专业学生。第二届模范引领计划数学与信息学院学习之星获得者。现任学院团委组织部干事，现任班级团支书。', 'http://7xlp0l.com1.z0.glb.clouddn.com/146509582000031.png', 1465095976, 1465095976, '唐雪妹', '模范引领', '锲而不舍 奋力向前', '<section class="yead_editor" label="Copyright © 2015 Yead All Rights Reserved." style=""><section style="max-width: 100%; box-sizing: border-box; border: 0px none; font-family: 微软雅黑; word-wrap: break-word !important;"><section style="margin: 10px auto; max-width: 100%; box-sizing: border-box; border: 1px solid rgb(197, 200, 204); text-align: center; border-top-left-radius: 5px; border-top-right-radius: 5px; border-bottom-right-radius: 5px; border-bottom-left-radius: 5px; box-shadow: rgb(255, 255, 255) 0px 1px 0px, rgb(197, 200, 204) 0px 2px 0px, rgb(255, 255, 255) 0px 3px 0px, rgb(197, 200, 204) 0px 4px 0px; word-wrap: break-word !important;"><section style="max-width: 100%; box-sizing: border-box; height: 25px; border-bottom-width: 0px; border-bottom-style: solid; border-bottom-color: rgb(197, 200, 204); border-top-left-radius: 4px; border-top-right-radius: 4px; border-bottom-right-radius: 0px; border-bottom-left-radius: 0px; background-color: rgb(219, 219, 221); word-wrap: break-word !important;"><section style="margin-right: 100px; max-width: 100%; box-sizing: border-box; display: inline-block; height: 14px; width: 14px; border-top-left-radius: 7px; border-top-right-radius: 7px; border-bottom-right-radius: 7px; border-bottom-left-radius: 7px; background-color: rgb(173, 173, 173); word-wrap: break-word !important;"><section style="margin-top: 30px; max-width: 100%; box-sizing: border-box; height: 14px; width: 14px; border-top-left-radius: 7px; border-top-right-radius: 7px; border-bottom-right-radius: 7px; border-bottom-left-radius: 7px; background-color: rgb(222, 222, 222); word-wrap: break-word !important;"></section><section style="margin-top: -37px; margin-left: 2px; max-width: 100%; box-sizing: border-box; width: 8px; height: 30px; border: 1px solid rgb(202, 202, 