--
-- 表的结构 `sy_news`
--

CREATE TABLE IF NOT EXISTS `sy_news` (
  `id` int(5) NOT NULL AUTO_INCREMENT,
  `subject` varchar(128) NOT NULL,
  `lastmodifytime` int(64) NOT NULL,
  `author` varchar(64) NOT NULL,
  `message` text NOT NULL,
  `createtime` int(128) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`),
  UNIQUE KEY `id` (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=13 ;

--
-- 转存表中的数据 `sy_news`
--

INSERT INTO `sy_news` (`id`, `subject`, `lastmodifytime`, `author`, `message`, `createtime`) VALUES
(6, '“诚信像金子般珍贵”——学院学生诚信教育讲座顺利召开', 1464855553, '思源宣传部', '<p><span style="font-size:18px;font-family:宋体;">10月30日中午</span><span style="font-size:18px;font-family:宋体;">，我院诚信教育讲座暨</span><span style="font-size:18px;font-family:宋体;">2015</span><span style="font-size:18px;font-family:宋体;">年</span><span style="font-size:18px;font-family:宋体;">国家高校助学贷款签署合同会议</span><span style="font-size:18px;font-family:宋体;">在</span><span style="font-size:18px;font-family:宋体;">院楼</span><span style="font-size:18px;font-family:宋体;">201</span><span style="font-size:18px;font-family:宋体;">召开</span><span style="font-size:18px;font-family:宋体;">。学院统筹奖助贷工作的辅导员</span><span style="font-size:18px;font-family:宋体;">刘树鑫老师</span><span style="font-size:18px;font-family:宋体;">、</span><span style="font-size:18px;font-family:宋体;">思源工作室工作人员以及</span><span style="font-size:18px;font-family:宋体;">获得</span><span style="font-size:18px;font-family:宋体;">助学贷款</span><span style="font-size:18px;font-family:宋体;">资格</span><span style="font-size:18px;font-family:宋体;">的同学</span><span style="font-size:18px;font-family:宋体;">均</span><span style="font-size:18px;font-family:宋体;">参加</span><span style="font-size:18px;font-family:宋体;">了</span><span style="font-size:18px;font-family:宋体;">此次会议。</span></p><p style="text-align:center;"><span style="font-size:18px;font-family:宋体;"><img alt="" src="http://7xlp0l.com1.z0.glb.clouddn.com/14648555493.png" border="0"/></span></p><p style="text-align:center;"><span style="font-size:18px;font-family:宋体;">郑壮钦精彩发言</span></p><p><span style="font-size:18px;font-family:宋体;">&nbsp;&nbsp;&nbsp;&nbsp;“诚信像金子般珍贵”，</span><span style="font-size:18px;font-family:宋体;">思源工作室副主任郑壮钦结合自己在大学的学习体会和生活经历，</span><span style="font-size:18px;font-family:宋体;">通过生动详实的描述，给到会的同学上了一场“当信用消失的时候，肉体就没有生命”的“心灵鸡汤”式讲座，获得了阵阵掌声。</span></p><p style="text-align:center;"><span style="font-size:18px;font-family:宋体;"><img alt="" src="http://7xlp0l.com1.z0.glb.clouddn.com/14648555507.png" border="0" height="337" width="510"/></span></p><p style="text-align:center;"><span style="font-size:18px;font-family:宋体;">刘老师和思源工作室细心指导</span></p><p style="text-indent:21pt;"><span style="font-size:18px;font-family:宋体;">最后，同学们在老师的细致指导下均顺利地签署了每一份承载着大学梦想的贷款合同。在学院诚信教育的熏陶下，希望全体同学走正直诚实的生活道路，找到一个问心无愧的归宿，做一个诚信自强的华农人！</span></p><p style="text-indent:21pt;"><span style="font-size:18px;font-family:宋体;">（文<span style="font-size:18px;font-family:&#39;Times New Roman&#39;;">/</span><span style="font-size:18px;font-family:宋体;">图&nbsp;胡奕华&nbsp;刘尧方&nbsp;思源工作室）</span></span></p><p><br/></p>', 0),
(7, '饮水思源，感恩传递', 1464855725, '思源宣传部', '<p style="text-align:justify;text-indent:21pt;"><span style="font-size:18px;font-family:微软雅黑;">11月1日晚，2015年思源工作室换届大会暨大学生感恩教育活动在数学与信息学院院楼500会议室举行。学院思源工作室的指导老师、学院辅导员、思源工作室全体成员、学院其他学生组织代表，兄弟学院勤工助学组织代表、各年级生委及受助生代表均参加了会议。</span></p><p style="text-align:center;text-indent:21pt;"><span style="font-size:18px;font-family:微软雅黑;"></span></p><p style="text-align:center;text-indent:21pt;"><span style="font-size:18px;font-family:微软雅黑;"><img alt="" src="http://7xlp0l.com1.z0.glb.clouddn.com/14648556958.png" border="0" height="450" width="600"/></span></p><p style="text-align:center;text-indent:21pt;"><span style="font-size:12px;font-family:微软雅黑;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 思源工作室展示风<span style="font-size:14px;">采</span></span></p><p style="text-align:justify;text-indent:21pt;"><span style="font-size:18px;font-family:微软雅黑;font-weight:bold;">饮水思源是中华民话的传统美德</span></p><p style="text-align:justify;text-indent:21pt;"><span style="font-size:18px;font-family:微软雅黑;">大会在庄严的国歌声中拉开帷幕后，思源工作室指导老师刘树鑫在讲话中感谢大家对学院奖助贷工作和思源工作室成长的关心和支持，肯定了思源工作室在上一学年为学院奖助贷工作有序开展的所做出的努力和付出，并希望到会的同学要饮水思源，做一个感恩的人。</span></p><p style="text-align: center; text-indent: 21pt;"><span style="font-size:18px;font-family:微软雅黑;"><img alt="" src="http://7xlp0l.com1.z0.glb.clouddn.com/14648556966.png" border="0" height="450" width="600"/></span></p><p style="text-align: center; text-indent: 21pt;"><span style="font-size:18px;font-family:微软雅黑;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="font-size:14px;"> 会旗交接仪式</span></span></p><p style="text-align:justify;text-indent:21pt;"><span style="font-size:18px;font-family:微软雅黑;font-weight:bold;">团结奋进中展望未来</span></p><p style="text-align:justify;text-indent:21pt;"><span style="font-size:18px;font-family:微软雅黑;">通\r\n过思源工作室主任团和各部门视频风采的播放，不仅增进了全体思源人的友谊和感情，也让到会的生活委员和受助生们了解到思源工作室开展奖助贷服务工作的流\r\n程。思源工作室前任主任郑桂荣在讲话中指出，从之前的助学管理委员会到如今的思源工作室，经历了一个充实而美好的成长过程。思源工作室的新主任邹志莲则表\r\n示将继往开来，创新服务思路，以服务师生为目的，迎接挑战，努力奋斗！</span></p><p style="text-align:justify;text-indent:21pt;"><span style="font-size:18px;font-family:微软雅黑;">&nbsp;</span></p><p style="text-align:center;text-indent:21pt;"><img alt="" src="http://7xlp0l.com1.z0.glb.clouddn.com/146485569610.png" border="0" height="450" width="600"/></p><p style="text-align:center;text-indent:21pt;">&nbsp;<span style="font-size:14px;">王朝鹏同学发表精彩演说</span></p><p style="text-align:justify;text-indent:21pt;"><span style="font-size:18px;font-family:微软雅黑;font-weight:bold;">让感恩也成为一种模范引领</span></p><p style="text-align:justify;text-indent:21pt;"><span style="font-size:18px;font-family:微软雅黑;">&nbsp;&nbsp;&nbsp;2014\r\n级计算机3班的受助生代表王朝鹏同学（同时也是2014-2015学年度国家奖学金的获得者）结合自己受助的情况和发奋图强的实际经历，表达了对国家、学\r\n校和学院的感激之情，同时也呼吁在场的每一名同学要学会做一个感恩的人，不要去抱怨命运的安排，而是要挑战自己的命运，自强不息！</span></p><p style="text-align:justify;text-indent:21pt;">&nbsp;</p><p style="text-align: center; text-indent: 21pt;"><span style="font-size:18px;font-family:微软雅黑;">&nbsp;<img alt="" src="http://7xlp0l.com1.z0.glb.clouddn.com/14648556972.png" border="0" height="450" width="600"/></span></p><p style="text-align: center; text-indent: 21pt;"><span style="font-size:18px;font-family:微软雅黑;"><span style="font-size:14px;"></span><span style="font-size:14px;">大合照</span></span></p><p style="text-align:justify;text-indent:21pt;"><span style="font-size:18px;font-family:微软雅黑;">&nbsp;&nbsp;&nbsp;至此，思源工作室换届大会圆满结束，但感恩如同一股暖流，对每一个到会的人都产生了爱与成长的余热！</span><span style="font-size:18px;font-family:仿宋;">（文/图&nbsp;李艳红&nbsp;赵成&nbsp;思源工作室）</span></p><p><br/></p>', 0),
(8, '创业之星放光芒，模范引领进课堂', 1464855759, '思源宣传部', '<p><span style="font-size:18px;font-family:&#39;宋体&#39;;">近日，学院第二届本科生“模范引领计划”风采展示暨答辩评审会于院楼<span style="font-size:18px;font-family:Calibri;">140</span><span style="font-size:18px;font-family:宋体;">顺利举行，</span><span style="font-size:18px;font-family:Calibri;">多</span><span style="font-size:18px;font-family:宋体;">名优秀学子和</span><span style="font-size:18px;font-family:宋体;">先进集体“八仙过海，各显神通”在众多竞争者中脱颖而出，成为该院十大之星。他们凭高尚的品德，用优异的成绩，以感人的事迹，通过演讲、表演、现场互动等多样化的精彩展示，演绎先进的旋律，散发模范的芬芳，给学院披上了灿烂星光的“外衣”。</span></span></p><p><img title="20151206_1449407522150093479.jpg" alt="1447944383129.jpg" src="http://7xlp0l.com1.z0.glb.clouddn.com/14648557501.png"/></p><p style="text-indent:21.75pt;"><span style="font-size:18px;font-family:&#39;宋体&#39;;">其中，创业之星尹国龙一马当先，于<span style="font-size:18px;font-family:Calibri;">11</span><span style="font-size:18px;font-family:宋体;">月</span><span style="font-size:18px;font-family:Calibri;">11</span><span style="font-size:18px;font-family:宋体;">号晚上将模范引领带进东区第四教学大楼</span><span style="font-size:18px;font-family:Calibri;">502</span><span style="font-size:18px;font-family:宋体;">的课堂上，给</span><span style="font-size:18px;font-family:Calibri;">2015</span><span style="font-size:18px;font-family:宋体;">级信息科学与技术专业的新生上了一场题为“创业者说”的精彩创业指导课。</span></span></p><p style="text-align:center;text-indent:21.75pt;"><span style="font-size:18px;font-family:&#39;宋体&#39;;color:#ff0000;"><span style="font-size:18px;font-family:Calibri;"></span></span></p><p><img title="20151206_1449407542566096954.jpg" alt="1447944383129.jpg" src="http://7xlp0l.com1.z0.glb.clouddn.com/14648557512.png"/></p><p><span style="font-size:18px;font-family:&#39;宋体&#39;;">&nbsp;&nbsp;&nbsp;“创业需要勇气和决心”，来自<span style="font-size:18px;font-family:Calibri;">2012</span><span style="font-size:18px;font-family:宋体;">级信息科学与技术专业，同时也是新燃点网络科技公司创始人兼</span><span style="font-size:18px;font-family:Calibri;">CEO</span><span style="font-size:18px;font-family:宋体;">的\r\n尹国龙在发言中表示，大学生创业除了需要必要的资金、技术、团队以及家人的支持外，还需要一颗敢于创业、执着创业的心。他结合自己丰富的创业经历，向到场\r\n的学弟学妹们提出三点建议：一是评估自己，明确目标。“条条大路通罗马，只是方式不尽然”，只有正确地掌握自己的特点，将创业所要实现的目标清晰地烙在心\r\n理，才能承受住创业初期较高机会成本带来的心理压力；二是持之以恒，永不放弃。每次创业失败的经历都是宝贵的经验，当经验积累到一定程度后就会转发为创业\r\n成功的方法。三是注重过程，问心无愧。大学生在创业的过程中必然会经历“山重水复疑无路”的茫然与失落，但是只要问心无愧，善于学习，把握机遇，创业道路\r\n必然迎来“柳暗花明又一村”。</span></span></p><p><img title="20151206_1449407570595014796.jpg" alt="1447944383129.jpg" src="http://7xlp0l.com1.z0.glb.clouddn.com/14648557529.png"/></p><p style="text-indent:24pt;"><span style="font-size:18px;font-family:&#39;宋体&#39;;">课后，同学们纷纷聚在创业之星周围，向模范取经，希望为各自的创业梦想添砖加瓦，找到一条属于自己的“星光大道”。</span></p><p style="text-indent:24pt;"><span style="font-size:18px;font-family:&#39;宋体&#39;;">据悉，</span><span style="font-size:18px;font-family:&#39;宋体&#39;;">我</span><span style="font-size:18px;font-family:&#39;宋体&#39;;">院\r\n在本次模范引领的宣传过程中所采用的宣传网站和微信推送平台都由受助生所开发和设计，并通过线上线下、课内课外的宣传方式，点面结合，实现感恩教育、励志\r\n强能教育、创新创业教育、先进模范教育的有机结合，对弘扬校园正气，倡导文明新风，激励学生积极进取、创先争优，起了重要的作用。（文</span><span style="font-size:18px;font-family:&#39;宋体&#39;;">/</span><span style="font-size:18px;font-family:&#39;宋体&#39;;">刘树鑫</span><span style="font-size:18px;font-family:&#39;宋体&#39;;">&nbsp;图<span style="font-size:18px;font-family:Calibri;">/</span><span style="font-size:18px;font-family:宋体;">郑壮钦&nbsp;陈嘉兴&nbsp;容凤晴&nbsp;团委学生会&nbsp;思源工作室&nbsp;</span></span><span style="font-size:18px;font-family:&#39;宋体&#39;;">）</span></p><p><br/></p>', 0),
(9, '星光闪耀，展青春风采', 1464855802, '思源宣传部', '<p><span style="font-family:宋体;"><span style="font-size:small;"></span><span style="font-size:small;">12</span></span><span style="font-family:宋体;"><span style="font-size:small;">月23</span><span style="font-size:small;">日晚，第二届本科“模范引领计划”颁奖典礼在数学与信息学院院楼</span><span style="font-size:small;">500</span><span style="font-size:small;">举行。出席本次典礼的嘉宾有学院辅导员老师、获奖学生以及学生观众。在庄严的国歌声中，典礼拉开序幕。</span><span style="font-size:small;">&nbsp; </span></span></p><p>&nbsp;</p><p style="text-align:center;"><span style="font-size:small;"><span style="font-family:宋体;"><img alt="" src="http://7xlp0l.com1.z0.glb.clouddn.com/14648557997.png" border="0" height="450" width="600"/>&nbsp; </span></span></p><p>&nbsp;</p><p><strong><span style="font-size:14pt;font-family:宋体;">&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></strong><strong><span style="font-size:14pt;font-family:宋体;">筑梦起航，扬学子志向</span></strong></p><p><span style="font-size:small;"><span style="font-family:宋体;">&nbsp;&nbsp;&nbsp; 十位恒星，九位卫星，十一位提名之星，闪闪发光。他们用求索和实践勾勒人生蓝图，用勤奋和汗水谱写青春华章，用实际行动为数信学子树立了优秀榜样，点亮自我，更照</span><span style="font-family:宋体;">亮了他人</span><span style="font-family:宋体;">。</span></span></p><p style="text-align:center;"><span style="font-size:small;"><span style="font-family:宋体;"><img alt="" src="http://7xlp0l.com1.z0.glb.clouddn.com/14648558000.png" border="0" height="450" width="600"/></span></span></p><p style="text-align:center;"><span style="font-size:small;"><span style="font-family:宋体;"><img alt="" src="http://7xlp0l.com1.z0.glb.clouddn.com/14648558007.png" border="0" height="450" width="600"/></span></span></p><p><strong><span style="font-size:14pt;font-family:宋体;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></strong><strong><span style="font-size:14pt;font-family:宋体;">奋力向前，持之以恒</span></strong></p><p>&nbsp;</p><p><span style="font-family:宋体;"><span style="font-size:small;">&nbsp;&nbsp;&nbsp; 星星代表人物王元昊、王朝鹏发表了获奖感言，发言的内容主要是感谢指导老师与学院领导的关怀与教育，并且还表达了将继续努力、不断完善自我、超越成功极限的积极态度，在他们的发言中可知人生价值的实现在于勇敢尝试，持之以恒。继续前行，星闪之光，不会只在一时！</span></span></p><p style="text-align:center;"><span style="font-family:宋体;"><span style="font-size:small;"><img alt="" src="http://7xlp0l.com1.z0.glb.clouddn.com/14648558015.png" border="0" height="450" width="600"/></span></span></p><p>&nbsp;</p><p><span style="font-size:small;"><span style="font-family:宋体;">&nbsp;&nbsp;&nbsp; </span><span style="font-family:宋体;">最后，晚会在热烈的掌声中落下帷幕。通过此次颁奖晚会，不仅对十大星星的优异表现给予了肯定，更是极大鼓舞了同学们学习、工作的积极性，让我们向他们看齐，一起携手努力拼搏，为学院、为人生的明天再创辉煌！</span></span><span style="font-size:12pt;font-family:宋体;">（文/图&nbsp;江莉娜&nbsp;赵成&nbsp;思源工作室）</span></p><p><br/></p>', 0),
(10, '厚植“助学、筑梦、铸人”精神——我院受助生到番禹区进行网络文明宣传活动', 1464855867, '思源宣传部', '<p style="text-indent:40px"><span style="font-size:20px;font-family:宋体">为进一步提升我院资助工作的成效，厚植</span><span style="font-family: 宋体; font-size: 20px;">“助学、筑梦、铸人”精神</span><span style="font-family: 宋体; font-size: 20px;">，近日学院思源工作室主办的“文明上网、人人有责”的宣传活动在广州市番禺区顺利举行。学院受助生纷纷积极参加了这个宣传活动。</span></p><p style="text-indent:40px"><span style="font-size:20px;font-family:宋体">虽然广州的“回南天”让城市的大街小巷都湿漉漉的，但参加宣传活动的学生均热情不减。他们不仅将传单派给每一个路人，还细心地对他们讲解“互联网</span><span style="font-size:20px">+</span><span style="font-size:20px;font-family:宋体">”时代下，网络道德、网络文明对推动经济发展、社会进步、和谐民生的重要性。</span></p><p style="text-indent:40px"><span style="font-size:20px;font-family:宋体"></span></p><p style="text-align: center;"><img src="http://7xlp0l.com1.z0.glb.clouddn.com/14648558503.png" style="" title="20160321_1458547961746001930.jpg"/></p><p style="text-align: center;"><img src="http://7xlp0l.com1.z0.glb.clouddn.com/14648558517.png" style="width: 945px; height: 481px;" title="20160321_1458547963395093048.jpg" height="481" width="945"/></p><p style="text-indent:40px"><span style="font-size:20px;font-family:宋体"></span><br/></p><p style="text-indent:40px"><span style="font-size:20px;font-family:宋体">活动结束后，学生们表示收获良多，并通过小组讨论、个人发言的形式，立志为网络文明建设尽自己最大的力量。（文</span><span style="font-size:20px">/</span><span style="font-size:20px;font-family:宋体">图</span> <span style="font-size:20px;font-family:宋体">郑壮钦</span> <span style="font-size:20px;font-family: 宋体">思源工作室</span> <span style="font-size:20px;font-family:宋体">）</span></p><p><br/></p>', 0),
(11, '学院举行奖助贷政策专题讲座', 1464855923, '思源宣传部', '<p style="text-indent:37px;line-height:150%"><span style="font-size:19px;line-height:150%;font-family: 宋体">近日，为进一步提升资助育人的工作成效，学院在院楼500报告厅举行奖助贷专题讲座。思源工作室的指导老师、学生干部以及学院部分贫困生都参加了此次讲座。</span></p><p style="text-align:center;text-indent:40px;page-break-after:avoid">&nbsp;</p><p style="line-height:150%"><span style="font-size: 19px;line-height:150%;font-family:宋体">&nbsp;&nbsp;&nbsp; </span><span style="font-size:19px;line-height:150%;font-family:宋体">在此次讲座中，陈嘉兴副主任通过对综合测评管理办法的相关问题以及注意事项进行详细的阐述，并结合自己的工作经验提出了对现行综合测评管理实施办法的一些修改意见；</span><span style="font-size:19px;line-height:150%;font-family:宋体">胡博婷副主任则除了补充说明校级奖学金的种类以及评奖人数在学院所占比例之外，还介绍了学院贫困生认定程序和助学金的一般评选流程，希望广大的贫困生能饮水思源、自立自强，努力提高自己的各方面素质。</span></p><p style="line-height: 150%; text-align: center;"><span style="font-size: 19px;line-height:150%;font-family:宋体">&nbsp;<img src="http://info.scau.edu.cn/upload/ueditor/image/20160410_1460286344336069762.png" title="20160410_1460286344336069762.png" alt="20160410_1460286344336069762.png" style="width: 654px; height: 378px;" border="0" height="378" vspace="0" width="654"/> </span></p><p style="line-height:150%"><br/></p><p><span style="font-size:16px">&nbsp;&nbsp;&nbsp; </span><span style="font-size:19px;font-family:宋体">最后，会议在邹志莲主任和思源五大部长的</span><span style="font-size:19px">3</span><span style="font-size:19px;font-family:宋体">月份工作总结中圆满落幕。</span><span style="font-size:19px;font-family: 宋体">（文/图 李艳红 思源工作室）</span></p><p><br/></p>', 0),
(12, '抱诚守真，感恩社会 ——我院2016届贷款毕业生诚信教育暨毕业教育讲座顺利进行', 1464855997, '思源宣传部', '<p style="text-indent: 37px"><span style="font-family: 宋体; font-size: 20px;">2016年<span style="font-size: 20px; font-family: Calibri;">5</span>月<span style="font-size: 20px; font-family: Calibri;">20</span>日中午，我院<span style="font-size: 20px; font-family: Calibri;">2016</span>届贷款毕业生诚信教育暨毕业教育讲座在数信与信息学院院楼<span style="font-size: 20px; font-family: Calibri;">500</span>会议室举行。学院思源工作室指导老师、<span style="font-size: 20px; font-family: Calibri;">2016</span>届贷款毕业生以及部分学生干部参加了本次活动。</span></p><p style="text-align: center;"><span style="font-size: 20px;"><strong><span style="font-family: 黑体; font-size: 16px;">&nbsp; &nbsp;&nbsp;<img src="http://info.scau.edu.cn/upload/ueditor/image/20160524_1464074994501061531.jpg" title="20160524_1464074994501061531.jpg" alt="IMG_0987.jpg" style="width: 480px; height: 541px;" height="541" width="480"/></span></strong></span></p><p style="text-align: center;"><span style="font-size: 20px;"><strong><span style="font-family: 黑体; font-size: 16px;"><img src="http://info.scau.edu.cn/upload/ueditor/image/20160524_1464075205142034891.jpg" title="20160524_1464075205142034891.jpg" alt="IMG_1021.jpg" style="width: 473px; height: 548px;" height="548" width="473"/></span></strong></span></p><p style="text-indent: 37px"><span style="font-family: 宋体; font-size: 20px;">活\r\n动中，思源工作室指导老师刘老师和主任邹志莲详细地为毕业生讲解了还款流程和注意事项，并告知毕业生还款过程中遇到问题应如何解决。为让即将开始还款的贷\r\n款毕业生迅速掌握还款方法，思源工作室还特意给每位贷款毕业生发放了学校统一印制的还款说明宣传手册，帮助贷款毕业生掌握诚信还款的流程。此外，学生代表\r\n连浩逵同学结合自己申请贷款亲身经历，表达了对国家、学校、学院的感恩之情，倡导大家积极制定贷款还款明确计划。思源工作室的学生干部则在讲座结束前细心\r\n地帮助现场每一位贷款毕业生填写《国家开发银行还款确认书》。</span></p><p style="text-indent: 37px; text-align: center;"><span style="font-family: 宋体; font-size: 20px;">&nbsp;<img src="http://info.scau.edu.cn/upload/ueditor/image/20160524_1464075298952067449.jpg" title="20160524_1464075298952067449.jpg" alt="IMG_1064.jpg" style="width: 502px; height: 415px;" height="415" width="502"/></span></p><p><span style="font-family: 宋体; font-size: 20px;">&nbsp;&nbsp;&nbsp;&nbsp;本\r\n次教育讲座以“弘扬中华民族传统美德，增强大学生诚信感恩意识”为核心，不仅让贷款毕业生正确认识国家助学贷款政策和诚信还款与个人信用的密切关系，而且\r\n宣扬了“抱诚守真，感恩社会”的精神，激励毕业生们怀着感恩之心，背起诚信之名，踏出大学之门，坚定地走向未来！（文<span style="font-size: 20px; font-family: Calibri;">/</span>图<span style="font-size: 20px; font-family: Calibri;">&nbsp;</span>赵成 思源工作室）</span></p><p><br/></p>', 0);

